.baseRedBg a.headlineDividerCTA::before,
.howItWorks .redNHBtn::before,
.selectTireSize .submitButton button::before,
.vehicle-selector #shopVehicleSearchBtn::before,
	
a.greyRdBtn:link::before,
a.redHover::before
{
	background: radial-gradient(circle, #000 0%, #d90008 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

