css:
@-moz-document url-prefix("http://tumblr.com"), url-prefix("http://www.tumblr.com"), url-prefix("https://tumblr.com"), url-prefix("https://www.tumblr.com") {
.identity{background-color:#000}
.ui_dialog_lock{background:#000}
.l-header-container--refresh{background-color:#000}
.l-container.l-container--two-column-dashboard .l-content,.l-container.l-container--two-column .l-content{background-color:#000}
.l-container.l-container--two-column-dashboard .right_column,.l-container.l-container--two-column .right_column{background-color:#000}
.l-container.l-container--two-column-dashboard .left_column{background-color:#000}
.plus-follow-button{color:#000}
.post_avatar{background-color:#000}
.identity .controls_section.user_list li .follow_list_item_blog:before{background-image:linear-gradient(90deg,rgba(0,0,0,0),#000);border-right:5px solid #000}
.discover_actions_discover .elevator,.search_actions_search .elevator{background-color:#00b8ff}
.post-forms-glass{background-color:rgba(0,0,0,.95)}
.blue#glass_overlay,.blue#hello_glass,.blue.glass{background-color:rgba(0,0,0,.95)}
.identity .right_column:after{background:linear-gradient(180deg,#000,rgba(0,0,0,0))}
.notifications_page .section.divider{background:transparent url(data:)}
.l-container.l-container--flex .l-content{background-color:#000}
.post_avatar .post_avatar_link{background-color:#000}
.search_results_container .tumblelog_mask_item{background-color:#000}
.radar .radar_footer .radar_avatar::before{background-image:linear-gradient(to right,rgba(0,0,0,0),#000)}
.notification,.notification *,.toastr .toast,#toaster .toast.toast_image_text_image .toast_inner .txt a,#toaster .toast.toast_image_text_image .toast_inner .txt,#toaster .toast.toast_image_text_icon .toast_inner .txt,.notification .notification_sentence .username,.notification .notification_sentence .banner_text,.notification .notification_sentence a,.subheading,#toaster .toast .toast_inner *{background-color:#000}
.post_full .post_permalink{border-top-color:#000;border-right-color:#000}
.rapid-recs-container .rapid-recs{background-image:linear-gradient(180deg,rgba(0,0,0,0) 0,#000 15%,#000 85%,rgba(0,0,0,0))}
.identity .controls_section.user_list li:active .follow_list_item_blog:before,.identity .controls_section.user_list li:hover .follow_list_item_blog:before{background-image:linear-gradient(90deg,rgba(18,18,18,0),#121212);border-right:5px solid transparent}
.compose-button{fill:#ff72d3}
.reblog_follow_button .follow-text,.worded-follow-button .follow-text{color:#896dff}
.identity .controls_section .item:hover .follow:hover{background:#896dff}
.ui_peepr_glass{background-color:rgba(0,0,0,.95)}
.tab_notice .tab_notice_value{color:#000}
.tab-bar-container .tab_notice{color:#000}
.tab-notice--outlined{border-color:#000}
.dashboard_options_form,.tag_psa{border:1px solid #1f1f1f}
}
