}

/* --------------------- BAK Defaults ---------------------

:root
{
    --ArchiveLinks: #9B8440;
    --ArchiveLinksHover: #DABD73;
    --formSectionContentBackgroundColor: #111;
}

/* --------------------- BAK Defaults --------------------- */


::-moz-selection,
::selection
{
    background: #aaa !important;
    color: #000 !important;
}

:focus
{
    outline: none !important;
    transition: all 0.4s ease-in-out !important;
}

/*html, - Disabled 20200426 for sci-hub */
/*body, - Disabled 20200425 after update*/
#wrap
{
    height: 1%;
}


:root,
span,
b,
em,
i,
#descript,
#error h2,
body.ia-module .stealth,
.month .month-day-container,
.site
{
    background: #0000;
    background-color: #0000 !important;
    color: #999 !important;
}

body.navia #nav-tophat .wayback-main .typeahead a.dropdown-item,
.wikipedia-summary
{
    color: #999!important;
}


.boxy-ttl
{
    color: white !important;
    text-shadow: none !important;
}

p .show-all,
.dropdown-menu > li > a:focus,
.dropdown-menu > li > a:hover,
.btn-info
{
    color: white !important;
    background: var(--ArchiveLinks) !important;
    border-color: #333 !important;
    text-shadow: none !important;
    transition: all 0.4s ease-in-out !important;
}
.btn-info:hover
{
    color: #000 !important;
    background: var(--ArchiveLinksHover) !important;
}
.btn-info:active,
body.navia .searchbar .btn-gray:active
{
    filter: brightness(.6) !important;
    transition: 0s !important
}


