    text-decoration: underline !important;
    transition: all 0.4s ease-in-out;
}

body.ia-module .columns-items .range-maker > div table tr td a:hover
{
    color: var(--ArchiveLinksHover) !important;
    text-decoration: overline underline !important;
    transition: all 0.2s ease-in-out;
}

a:hover,
a:hover *,
body.ia-module .columns-items .range-maker > div table tr td a:hover,
body.ia-module .stealth:hover,
body.top center.mt-big div.mt-big:hover > div > span,
body.top center.mt-big div.mt-big .micro-label:hover,
body.top center.mt-big div.mt-big:hover .micro-label
{
    color: var(--ArchiveLinksHover) !important;
    transition: all 0.4s ease-in-out;
}


a:active,
a:active *,
#flag-button:active,
#share-button:active,
.favorite-btn .button:active,
