css:
@-moz-document domain("tvhay.org") {
/*20190830*/

#body-wrap {
	width: 100%;
	display: grid;
	grid-template-columns: 5fr 1fr;
}
#page-watch {
	display: grid;
	grid-template-columns: 3fr 1fr;
}
#page-info .info .col2, #page-info .info .col1, #page-info .info {
	display: grid;
	grid-template-columns: 1fr 3fr;
    grid-auto-rows: max-content;
}
#page-info > .blockbody {
    display: grid;
    grid-template-columns: 4fr 3fr;
}
ul.list-film {
	display: grid;
	grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
}
#wpcomm .wc-thread-wrapper {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-gap: 20px;
}
.wpdiscuz-comment-pagination {
    grid-column: 1/span 2;
}
#movie-update, #comment {
	grid-column: 1/span 2;
}
#page-info .info .name2 {
    grid-column: 2;
}
#wpcomm {
    max-width: unset;
}
#footer .col5 {
    width: unset !important;
    float: left;
}
#page-info .info .poster {
	width: unset;
	position: unset;
	top: unset;
	left: unset;
	grid-row: 1/span 4;
    align-self: center;
}
#search, #page-info .info .col2, #page-info .info .col1, .container, #sidebar, #content {
	width: unset;
	float: unset;
}
#movie-hot {
	width: 100% !important;
	height: 350px;
}
#page-info .info .poster img, ul.list-film li {
	width: auto;
}
ul.list-film li img {
	width: 100%;
	height: 450px;
}
#page-watch #media {
	width: 100%;
	height: 550px;
}
#footer .container {
    width: 80%;
}
#nav .sub-menu {
    width: 450px;
}
.block1 [class^="col"] {
    width: 250px !important;
}
#nav .sub-menu li a {
    width: 200px;
}
#search, ul.list-film li .name, ul.list-film li .name2, .breadcrumbs .item {
	height: unset;
}
#page-info .info .poster img {
	height: max-content;
    max-height: 500px;
}
#movie-hot .listfilm li {
	height: 350px !important;
	width: auto !important;
}
#movie-hot .listfilm img {
	height: 350px;
	width: auto;
}
ul.list-film li .info {
    height: 100px;
}
#search .submit {
    height: 41px;
}
.block .blocktitle {
    height: 37px;
}
#wpcomm .wc_comm_form.wc_main_comm_form .wc-field-textarea .wpdiscuz-textarea-wrap textarea, .block .blocktitle .title, .block .blocktitle {
	font-size: unset !important;
}
.title, #footer, #nav .sub-menu li a, #nav li, #nav h2, #nav h3, #nav h4, #search .keyword, .wp-pagenavi a, .wp-pagenavi span, #wpcomm .wc-field-textarea textarea, #wpcomm .wc-reply .wc-comment-right .wc-comment-text, #wpcomm .wc-reply .wc-comment-right .wc-comment-text *, #wpcomm .wc-load-more-submit, #wpcomm .wpd-form-row .wpdiscuz-item input[type="text"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="email"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="url"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="color"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="date"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="datetime"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="datetime-local"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="month"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="number"], #wpcomm .wpd-form-row .wpdiscuz-item input[type="time"], #wpcomm .wpd-form-row .wpdiscuz-item .wc-bin-captcha, #wpcomm button, #wpcomm input[type="button"], #wpcomm input[type="reset"], #wpcomm input[type="submit"], #wpcomm .wc-comment-right .wc-comment-text, #wpcomm .wc-comment-right .wc-comment-text *, #page-info .like-stats, #comment .tabs .tab, #page-info .info .name2 h3, #page-info .info dt, #page-info .info .col1, .breadcrumbs .item, font, #page-watch .note li {
	font-size: unset;
}
#page-info .info .title {
    font-size: 36px;
}
body {
	font-size: 24px;
}
#chart li .detail .name a, ul.list-film {
	font-size: 21px;
}
#wpcomm .wc-field-textarea textarea, #wpcomm .wc-reply .wc-comment-right .wc-comment-text, #wpcomm .wc-reply .wc-comment-right .wc-comment-text *, #comment .tabs .tab, ul.list-film li .name, ul.list-film li .name2, .breadcrumbs .item, #page-watch .note li {
	line-height: unset;
}
#nav li a {
    line-height: 41px;
}
#page-info .tabs-content, #page-info .info dd {
    line-height: 1.3;
}
#wpcomm .wc-reply.wc_comment_level-2 {
    margin-left: unset;
}
#page-info .info .fr {
    margin-left: 20px;
    align-self: center;
}
#page-info .btn-groups {
    margin-left: 20px !important;
}
#page-watch .note {
	margin-left: 0;
	margin-right: 0;
}
#page-info .info .col2 {
	padding-left: 8px;
}
#page-info .like-stats {
    padding-top: 6px;
    padding-bottom: 6px;
}
ul.list-film li .status {
	top: 425px;
}
#movie-hot .listfilm .status {
	top: 284px;
}
#movie-hot .next, #movie-hot .prev {
	top: 185px;
}
#page-info .info a {
    color: blue;
}
#page-info .info .name2 {
    color: #4CAF50;
    font-weight: 600;
}
#search .submit {
    background-size: 336px;
}
.block1 [class^="col"] a:hover {
    background: #2aa5f2;
    color: #fff !important;
    padding: 5px;
}
.block1 [class^="col"] a:hover, #wpcomm .wpd-form-col-left div:last-of-type, #wpcomm .wpd-form-col-right div:last-of-type, #wpcomm .wpdiscuz-textarea-wrap, #wpcomm .wpd-form-row .wpd-field, #wpcomm button, #wpcomm input[type="button"], #wpcomm input[type="reset"], #wpcomm input[type="submit"], #wpcomm .wc-footer-left .wc-reply-button, #wpcomm .avatar, #wpcomm .wc-load-more-submit {
    border-radius: 6px;
}
#search .keyword {
    border-top-left-radius: 6px;
    border-bottom-left-radius: 6px;
}
#search .submit {
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
}
.divider {
	display: none;
}
}

@-moz-document url("http://tvhay.org/") {
/*20190830*/

#content {
	display: grid;
	grid-template-columns: 5fr 1.3fr;
}
}
