css:
@-moz-document domain("uesp.net") {
  /* CHANGE BACKGROUND COLORS*/
  body,
  div#footer,
  .toc,
  #toc,
  #catlinks,
  div.pBody,
  table.wikitable > tr > th, table.wikitable > * > tr > th,
  ul#filetoc,
  .navbox-title,
  .navbox-group,
  pre, .mw-code,
  tr[bgcolor="#DEDEFF"],
  #mf-main-page div {
      background: #222 !important;
  }
  
  div#content,
  #p-cactions ul li a,
  #mf-main-page table tbody tr td,
  table.striped tr:nth-child(2n){
      background-color: #282828 !important;
  }
  
  #mf-main-page div {
      border-color: #282828 !important;
  }
  
  #searchBody {
      background-color: #333 !important;
  }
  
  #p-personal .pBody,
  #p-cactions .pBody {
      background-color: transparent !important;
  }
  
  #p-cactions li {
   background: transparent !important;   
  }
  
  code,
  table.wikitable th {
      background-color: #171717 !important;
  }
  
  table {
      background:  #202020 !important;
  }
  
  div.thumb div,
  li.gallerybox div.thumb {
      background-color: #1e1e1e !important;
  }
  
  .citation:target, .citation:target td {
      background-color:  #242424 !important;
  }
  
  /* CHANGE BORDER COLORS*/
  
  body,
  div#footer,
  .toc,
  #toc,
  #catlinks,
  div.pBody,
  #p-cactions ul li a,
  .mw-body,
  #p-cactions li,
  code,
  div.thumb div,
  html .thumbimage,
  ul#filetoc,
  .navbox-list,
  .navbox,
  #mf-main-page table tbody tr td,
  li.gallerybox div.thumb,
  pre, .mw-code,
  #toc, .toc, .mw-warning, .toccolours {
      border-color: transparent !important;
  }
  
  h1, h2, h3, h4, h5, h6 {
      border-bottom-color: #464646 !important;
  }
  
  hr {
      color: #464646 !important;
      background-color: #464646 !important;
  }
  
  table.wikitable > * > tr > th,
  table.wikitable > * > tr > td {
      border-color: #464646 !important;
  }
  
  .navbox-even {
      background:  #282828 !important;
  }
  
  [style*="black"] {
      border-color: #6c6c6c !important;
  }
  
  /* CHANGE TEXT COLORS*/
  
  a {
      color: #307aee !important;
  }
  .mw-body,
  h1, h2, h3, h4, h5, h6,
  body,
  code,
  #searchInput,
  table.wikitable,
  pre, .mw-code {
      color: #d0d0d0 !important;
  }
  
  table.wikitable th.racecolor1-nor, .racecolor1-nor,
  table.wikitable th.racecolor2-nor, .racecolor2-nor {
      color: #fff !important;
  }
  
  /* FONT SIZE */
  
  .portlet h3 {
      font-size: medium !important;
  }
  
  .portlet li {
      font-size: larger !important;
      padding-top: 3px !important;
  }
}
