css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gamedev.ru") {
/**
Alternative style for redesigned'19 gamedev.ru forum part (other parts are still crappy, meh)
/Dexus
**/

h1{font-size:1.3em !important; margin:0; padding:3px;}
h2{font-size:1.1em !important; margin:0; padding:3px;}
.overflow+.bound:not(.head){border-left:solid 5px #c0d0e0; padding-bottom:5px;}
div.pre{background-color:#f2f2f2 !important;margin-left:6px !important;}

div.path {display:none;}
div.path:nth-of-type(2){display:block;background-color: #f8f4f0 !important; font-size:1.1em; margin-left:-2.7em; margin-top:-37px; height:30px !important; width:100%; position:fixed !important;}
div.path>div.bound{margin:0;padding:0;border-bottom:2px solid #e4e0dc;}
div.path a:link {padding:3px;}
div.path span a{padding:6px !important;}
body {font-size:0.8em; background-color: #fffcf8; margin: 0; padding-left:14.7em !important; padding-top:85px !important; padding-right:3em !important; max-width:100%; width:100%;}
div.bound{max-width: unset !important; padding-left:0.5em !important;}
div.clear {border:0 !important;background-color:rgba(0.5,0.5,0.5,0.05); margin-left:-7px !important;}
div.seo {display:none !important;}
	
body>div.bound>div:not(.rubric):not(.pages):not(.pic):not(.right):not(.sect) {display:block;padding:1px;margin:0;}
body>div.bound>div:not(.text):not(.rubric):not(.pages):not(.right):not(.sect):nth-of-type(odd) {border-left:4px solid #d0e0f0;}
body>div.bound>div:not(.text):not(.rubric):not(.pages):not(.right):not(.sect):nth-of-type(even) {background-color:rgba(0.5,0.5,0.5,0.05);border-left:4px solid #d0e0f0;}

div>span.row{font-size:0.8em;padding:0;margin:0; border-radius: 0; width:5em; padding-top:3px;font-weight:normal;}
div>div.row{font-weight: bold; display:block; overflow-y:auto; border:0;margin:0; padding:2px;}
div>div.row>br{content:" ";display:none;}
small>span.q {display:inline; float: right;font-weight:normal;}
small>span.q>a{font-size:1.15em; color:#4070c0 !important;}

div.docs,div.text,tr{font-size:0.93em;margin:0px;padding:0px;}
b.title{font-size:1.4em;}

div.bound>div.list{font-size:0.85em;padding:2px !important;margin:2px;}
div.bound>div.rubric{font-size:0.98em;margin:9px 0 0 0; padding:3px; padding-left:1em; background-color: #d0e0f0;}

div.sect{font-size:1.1em !important;padding-left:2em !important;background-color: #c0d0e0 !important;}

#menu div.button{display:none;}
div#menu {background-color: #f0ece6; display:block !important; margin-left:unset; left: 0;}
div#menu li>a{color:#5070a0;}
div#menu li.sel>a{color:#c04020;}
div#menu li{padding:3px 0 0 6px !important; font-weight: bold;}
span.tool{font-size:1.15em}

#tool{opacity: 1 !important; display:block !important;background-color: #80a0b0 !important;box-shadow:none !important;}
#enter-pos {background-color:transparent !important;padding-right:10px;}
a:hover.tool{background-color: #90b0c0 !important;}

div.head{border-left:solid 5px #c0d0e0; border-top:solid 2px #c0d0e0 !important; background-color: rgba(0.5,0.5,0.5,0.05); margin-top:1em !important;}
body>div.wide{margin-left:auto;}
body>div.overflow,body>div.wide{border-left:solid 5px #c0d0e0; padding-bottom:5px;overflow-wrap: break-word;}

div.quote{background-color:#f8f6f0 !important;margin-left:0.5em !important;padding:4px !important;border:0 !important; border-left:4px solid #d0e0f0 !important;}
div.offtop{padding-left:1em !important; margin-top:5px !important;}
div.head>div.pic{display:none !important;}

div[id^="spoiler"]:not([id^="spoilerHead"])>img,body>div.wide>img{border:0; max-width:99%;}
div[id^="spoiler"]:not([id^="spoilerHead"]){border-left:solid 8px #c0d0e0;padding:2px;}

div.footer{background-color: #fffcf8 !important;color:#a0a0a0;min-height:40px !important; margin:1em !important;}
div.footblock{display:block !important;text-align:center;margin:auto;}
div.footblock>a.gray{display:inline;color:#a0a0a0;}

div.pages{padding:8px !important;}
div.pages>a{background-color: #efece8 !important; padding:5px !important;}
div.pages>a:hover{background-color: rgba(0.5,0.5,0.5,0.1) !important;}

div.timePassed {color: #c0c0c0 !important;margin: 5px !important; font-size:1.4em!important; background:0 !important; border:0 !important;}

body>span.q{margin-top:2em; display:block;}
p.r{font-size:0.9em;}
div#advert,div#social,hr.forum{display: none;}
}
