css:
@-moz-document domain("lastpass.com") {
body,
#vault {
    background-color: #222 !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
}

#vaultContent,
.dialog,
.formFieldsView,
.contextMenu ul,
.dropdownMenu ul,
.selectDropdownList,
.optionsDropdown,
.optionButtons {
    background-color: #333 !important;
    color: #eee !important;
}

#options li:hover {
    background-color: #555;
    color: #eee !important;
}

.tile {
    background-color: #333;
    color: #eee !important;
    box-shadow: 0 1px 3px RGBA(255, 255, 255, 0.2);
}

.dialogRightPane,
.itemIcon {
    background-color: #222;
    color: #eee !important;
}

.stickyFolder {
    background-color: #444;
    color: #eee !important;
}

/* titles */
h1,
h2,
h3,
h4,
h5,
h6,
p,
#selectedItemCounter {
    color: #eee !important;
}

/* text selection */
::selection {
    background-color: #4183C4 !important;
    color: #fff !important;
}

::-moz-selection {
    background-color: #4183C4 !important;
    color: #fff !important;
}

/* inputs */
select,
input:not(.btn-link),
textarea {
    -moz-appearance: none !important;
    background-color: #25272a !important;
    border-color: #343434 !important;
    color: #eee !important;
}

/*BEGIN 2019-09-01*/
.list {
    background-color: #202020;
}

.list .bigIcon {
    filter: invert(87%);
}
/*END 2019-09-01*/
}
