css:
@-moz-document domain(cs-magick.ru) {
  body {
    margin: 0;
    color: #737373;
    background-image: url(https://www.tokkoro.com/picsup/1409206-video-game.jpg);
    background-size: cover;
  }

  #wrapper {
    margin: 20px 0 0;
  }

  #main-content {
    padding: 0 20px 10px;
  }

  #sidebar {
    padding-top: 20px;
    background-color: rgba(36, 72, 125, .9);
    box-shadow: 10px 0 20px rgba(0,0,0,0.19), 6px 0 6px rgba(0,0,0,0.23);
  }

  .sidebar-nav a {
    color: #fff;
  }

  .navbar-inverse{
    background-color: rgba(25, 91, 157,  .75);
  }
  
  .bg-dark {
    background-color: rgba(13, 57, 123, .7) !important;
    border-bottom: 1px solid rgba(255, 255, 255, .6) !important;
    box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
    position: relative;
  }
	
  .navbar-center {
    color: #fff;
  }
  
  a {
    color: #737373;
  }

  a:hover, a:focus {
    color: #08f;
  }

  .table-hover > tbody > tr:hover > td, .table-hover > tbody > tr:hover > th {
    border-bottom: solid 1px #007ab3;
    color: #1f5d79;
  }

  .avatar_chat img {
    box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  }

  .chatusess table tr td:nth-child(1) {
    margin-right: 8px;
    display: block;
  }
  
  .forum-questions .table.forum.table-striped.table-hover:last-of-type tr:last-child td {
    border: none;
  }
  
  .panel {
    box-shadow: 0 10px 20px rgba(0,0,0,0.19), 0 6px 6px rgba(0,0,0,0.23);
  }
  
  .panel .panel-footer .input-group {
    color: white;
  }
  
  .panel .panel-footer .input-group a {
    color: white;
  }
  
  .forum-questions {
    background-color: #f9fbffd6;
  }
  
  .panel-body {
    background-color: #f9fbffd6;
  }
  
  .panel-default {
    border-color: #000d2045;
  }
	
  #panel_default, .panel-footer {
    background-color: rgba(33, 42, 89, 0.5);
    border-top: 1px solid #2b2e33;
  }
  
  .table.forum.table-striped.table-hover tr th h3 {
    color: #6a6a6a;
  }
  
  .c-dark {
    color: #737373!important;
  }
  
  .table-hover td {
    border-bottom: 1px solid rgba(0,0,0,.1);
  }

  #mCSB_28 img {
    border-radius: 50%;
    box-shadow: 0 3px 6px rgba(0,0,0,0.16), 0 3px 6px rgba(0,0,0,0.23);
  }
  
  #chat_list {
    padding-top: 10px;
  }
}
