css:
@-moz-document url-prefix("http://streamm4u.com/") {
body {
    line-height: 1.42857;
    background-color: #343a40;
}

.alert-success {
    color: #fff;
    background-color: #2d3033;
    border-color: #262424;
}

.border {
    border: 3px solid #262424 !important;
}

.widget .widget-body {
    padding: 10px;
    background-color: #262424;
}

element {
    margin-bottom: -24px;
}

.topview {
    color: #b9b9b9;
}

.topview.active {
    color: #fff !important;
    background-color: #0000003b !important;
    font-size: 18px !important;
}

.table td, .table th {
    padding: .75rem;
    vertical-align: top;
    border-top: 1px solid #28282987;
}

.table {
    width: 200%;
    max-width: 400%;
    margin-bottom: 1rem;
    background-color: transparent;
}

.card {
    margin-bottom: 12px !important;
    width: auto;
    background-color: #202020;
}

h3.card-tit {
    font-size: .8rem;
    color: #ffffff78;
}

h3.card-tit, h4.card-tit {
    margin: .8px;
    text-align: center;
    font-weight: bold;
}

h4.card-tit {
    font-size: .7rem;
    color: #515151;
}

.footer {
    padding: 10px;
    margin: 10px 0;
    background-color: #31373d;
}
}
