css:
@-moz-document domain("compass.education") {
@keyframes ani {
    0% {
        opacity: 0;
    }
    100% {
      opacity: 1;
    }
}

@keyframes ani2 {
    0% {
        opacity: 0;
        transform: translatey(-100px);
    }
    100% {
        opacity: 1;
    }
}

/* Some general styling to make everything look better */
* { /* Everything! */
    border: none !important; /* Remove ugly borders everywhere */
    transition: background-color 0.05s, opacity 0.05s !important;  /* Add fadey background transitions to hoverable/clickable elements */
    letter-spacing: 0 !important;  /* Undo weird -1px and +1px letter spacing in random places */
}

.x-panel-header-text::after {
    content: "!!!";
}

.x-tab-bar-strip {
    display: none;
}

.x-window {
    animation: ani2 0.2s;
    border-radius: 3px;
}

.x-panel {
    animation: ani 0.5s !important;
}

.c_col * { /* The widgets and stuff, inside #c_bar */
    font-size: 12px !important;  /* Regulate weird font sizing */
}

/* Loading indicators */
.x-mask {
    background-color: black !important;
    border-radius: 4px;
    opacity: 0.1 !important;
}

.x-mask-msg, .x-mask-msg div {
    background-color: white !important;
}

.x-mask-msg {
    border-radius: 3px;
}

/* Backgrounds */
.greyBody { /* Background of website */
    background-color: #f8f9f9 !important;
    background-image: none; /* https://res.cloudinary.com/t56/image/upload/2-10.jpg */
    background-size: cover;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center;
}

#c_bar { /* Main content container */
    background: #fff0;
}

#container-1081 {
    background-color: white;
}

#CompassManagersActivityDefaultManager-body {
    background-color: white;
}

/* Removing some stuff we don't want */
#component-1079, #component-1080 { /* School and Compass logos */
    display: none !important;
}


#c_ftr, #ctl00_MenuBlob1_pnlSearch input { /* Footer and search bar */
    display: none;
}

.activity-header-filler-container { /* Weird gradientfiller thing */
    display: none;
}

/* Titles in navbar */
#c_main > table h1 { /* The h1 element located at the top of every page */
    position: fixed; /* Position the title fixed in the centre of navbar */
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    z-index: 999; /* Put it ontop of navbar (same z-index) */
    height: 32px; /* Same height as navbar */
    line-height: 32px;
    color: white;
    font-size: 18px;
}

.class-name-title { /* For subjects and classes pages */
    position: static; /* Instead of block */
    padding: 0;
    font-size: 18px;
    text-shadow: none;
}

.class-header-container {
    background: none !important; /* Remove grey background */
}

.class-code-title, .activity-header-container {
    display: none;
}

/* Fixing spacing at top of page */
#c_main > br {
    display: none;
}

.c_col, #c_main > table {
    margin: 0 !important;
}

#c_main {
    padding-top: 15px; /* Reduce padding */
}

/* Fixing user page titles */
#ctl00_cpO_UserActivityStatusBar {
    display: none;
}

#c_main > table a {
    color: white;
}

/* Navbar */
#mnuBlueBar {
    background-color: #2e466b;
    background-image: none;
    box-shadow: 0 0px 10px 0px #000B;
    animation: ani 0.2s;
}

#mnuMenuContainer.use_hover > ul > li:hover > a {
    background-color: #fff2;
}

#mnuMenuContainer > ul > li > ul {
    animation: ani 0.1s;
}

#mnuMenuContainer.use_hover > ul > li > a {
    padding: 4px 6px 4px 6px;
}

/* Navbar icons */
.menu-sprite {
    background-size: contain;
    background-position: 0px 0px;
    width: 24px;
    height: 24px;
    filter: invert(1);
    animation: ani 0.2s;
}

.menu-sprite-house { /* Home */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/action/home/materialiconsround/24px.svg)
}

.menu-sprite-calendar2 { /* Calendar */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/action/date_range/materialiconsround/24px.svg);
}

.menu-sprite-email { /* Email */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/communication/email/materialiconsround/24px.svg)
}

.menu-sprite-pen { /* Curriculum */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/image/edit/materialiconsround/24px.svg);
}

.menu-sprite-dots { /* Organisation */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/navigation/apps/materialiconsround/24px.svg);
}

.menu-sprite-community { /* Community */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/social/people/materialiconsround/24px.svg);
}

.menu-sprite-star { /* School favorites */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/toggle/star/materialiconsround/24px.svg);
}

.menu-sprite-user2 { /* User */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/social/person/materialiconsround/24px.svg);
}

.menu-sprite-gear { /* Tools */
    background-image: url(https://raw.githubusercontent.com/google/material-design-icons/master/src/navigation/more_horiz/materialiconsround/24px.svg);
}

/* Newsfeed */
.news-feed-item-widget-test-tag {
    animation: ani 0.2s;
}

#ext-comp-1074 {
    top: 0px !important; /* Positioning the newsfeed at the top because of removed logos */
}

#ext-comp-1064 {
    top: 25px !important; /* Positioning the newsfeed at the top because of removed logos */
}

#container-1077 div {
    width: 498px !important; /* Resizing the "My news" label to fit full width */
}

h5 {
    text-align: center;
}

#label-1075 {
    position: static !important;
}

.news-feed-item-widget-test-tag img {
    border-radius: 3px;
}

/* Calendar */
.ext-cal-evt { /* All events */
    /* border-color: #2951a588 !important; */ /* If you want to change the border color */
    border-radius: 3px !important;
    padding: 3px 6px !important; /* Add a bit of padding to text */
    /* box-shadow: 0px 1px 2px 1px #0001; /* Tiny shadows, optional */
    font-weight: normal !important; /* Make everything not bold */
    animation: ani 0.5s;
}

.ext-cal-day-today, .ext-cal-hd-days-tbl, .ext-cal-day-times {
    background: none !important; /* Remove ugly backgrounds at top of calendar for clean look */
}

.compass-day-calendar .ext-cal-hd-days-td tr:nth-child(2) .ext-cal-ev {
    padding-top: 2px !important; /* Reduce the padding on the top event in the calendar */
}

.ext-cal-day-time-inner {
    padding-right: 8px !important; /* Space the times on the calendar a bit to the left */
}

#calendardaywidget-1013-day-bd {
    background: none; /* Tiny little sliver of grey at the bottom of the calendar */
}

/* Widget shadows and round corners */
#container-1081, #calendardaywidget-1013, #taskListWidget-1032 { /* The "widgets" */
    box-shadow: 0px 0px 5px 5px #0000000A; /* Optional */
    border-radius: 4px;
}

#ext-comp-1009, #ext-comp-1009-innerCt, #container-1063, #container-1063-innerCt, #container-1073, #container-1073-innerCt, #dashboard-body, #dashboard-innerCt {
    overflow: visible; /* So we can see the shadows */
}

#activity-tabs, #container-1052, #taskListWidget-1032, #container-1136 {
    overflow: hidden; /* So we can see the round corners */
}

/* Widget white labels */
#toolbar-1014, #ext-comp-1074, #toolbar-1035 {
    box-shadow: 0 0 0 100px white inset;
}

#label-1075 > h5, #label-1036 > h5 {
    background-color: white !important;
}

#toolbar-1049, .x-column-header {
    background-color: white !important;
}
}

@-moz-document regexp("(.)+compass\\.education\\/(?=$)") {
#c_main h1:after {
    content: "Compass";
}
}
