@-moz-document domain("coub.com")
{
}
* {
  font-family: Asinastra, Helvetica, Arial, "Lucida Grande", sans-serif !important;
}

.body-container.night-mode 
{ 
  background-image: url(https://avatanplus.com/files/resources/original/593a9eeda566715c8cfcd06d.jpg) !important ; 
  background-size: 100% !important; 
  background-color: #0000 !important; 
  background-attachment: fixed !important; 
} 
.body-container
{
  background-image: url(https://avatanplus.com/files/resources/original/593a9eeda566715c8cfcd06d.jpg) !important ; 
  background-size: 100% !important; 
  background-color: #0000 !important; 
  background-attachment: fixed !important; 
}
.night-mode .coub-block-col, 
.night-mode .suggests, 
.night-mode .coub--timeline 
{
  position: relative;
}

.night-mode .coub-block-col:before, 
.night-mode .suggests:before, 
.night-mode .coub--timeline:before
{
  content: '';
  background-color: rgba(0,0,0,0.5) !important;
  display: inline-block;
  left: -5px;
  right: -5px;
  top: 0;
  bottom: 0;
  position: absolute;
}
.coub--timeline:before
{
  content: '';
  background-color: rgba(255,255,255,1) !important;
  display: inline-block;
  left: -5px;
  right: -5px;
  top: 0;
  bottom: 0;
  position: absolute;
}

.night-mode .coub-block-col {
  margin-top: 5px;
}

.night-mode .coub--timeline:first-child:before {
  top: -10px;
}

.night-mode .coub-block-col:before {
  top: -10px;
}

.night-mode .suggests:before {
  left: 3px;
  right: 3px;
}
