/* ==UserStyle==
@name           AWS Console Dark Theme
@namespace      github.com/openstyles/stylus
@version        1.1.1
@description    AWS console dark theme
@author         AndrisBorbas
@updateURL      https://raw.githubusercontent.com/AndrisBorbas/DarkSkins/master/aws/aws-dark.user.css
==/UserStyle== */
@-moz-document domain("console.aws.amazon.com") {

   :root {
      --link: #65b1ff;
   }

   body {
      background: #222;
      color: #eee;
   }

   #f {
      background-color: #00000000;
   }

   a,
   body a,
   .awsui a,
   .GC53E5CBO {
      color: var(--link);
   }

   .awsui p {
      color: #ccc;
   }

   select,
   textarea,
   input[type=text],
   .GC53E5CBNAE,
   .GC53E5CBM2E .GC53E5CBI3E span,
   .GC53E5CBM2E .GC53E5CBI3E,
   .GIQKP1XBQB .GIQKP1XIPB {
      background: #1a1a1a;
      color: #ccc !important;
   }

   .GC53E5CBM2E .GC53E5CBI3E div b {
      -webkit-filter: invert(100%) !important;
   }

   #awsgnav .awsc-services-search .awsc-services-search-filter a,
   #awsgnav #region-sel-not-required .service-name {
      color: #eee !important;
   }

   #awsgnav #regionMenuContent .unavailable-region {
      color: #777;
   }

   .gwt-SplitLayoutPanel-HDragger,
   .gwt-SplitLayoutPanel-VDragger,
   .GIQKP1XN1D .gwt-SplitLayoutPanel .gwt-SplitLayoutPanel-VDragger {
      background-color: #555;
      color: #eee;
   }

   #awsgnav .nav-menu-content,
   #awsgnav {
      background-color: #222;
      color: #eee;
   }

   #awsgnav #awsc-services-container ul li.awsc-header {
      filter: brightness(200%);
      color: #aaa;
   }


   #awsgnav #awsc-services-container li.service-item a,
   #awsgnav .service-link a,
   #awsgnav a.nav-menu,
   #awsgnav .service-category-link,
   #awsgnav #nav-history .service-label,
   #resourceGroupsMenuContent a,
   #usernameMenuContent.awsc-switch-role-support a,
   #awsgnav #regionMenuContent #region-sel-not-required {
      color: #bbb !important;
   }

   /*Route 53*/
   .GC53E5CBFMF,
   .GC53E5CBJGE {
      background: #222;
      color: #eee;
   }

   .aws-route53-side-navigation .nav-bar .nav-bar-row .nav-item {
      color: #bbb;
   }

   .aws-route53-side-navigation .nav-bar .nav-bar-row.nav-category {
      color: #eee;
   }

   .GC53E5CBHOE,
   .GC53E5CBKWD {
      color: #eee;
   }

   .GC53E5CBKOE,
   .GC53E5CBFOE a,
   .GC53E5CBEAB,
   .GC53E5CBG2D,
   .GC53E5CBG5 {
      background: #222;
      color: #bbb;
   }

   .GC53E5CBCCB,
   .GC53E5CBEMF,
   .GC53E5CBLGE {
      background: -webkit-linear-gradient(top, #333 0%, #444 90%);
      color: #ddd;
   }

   .GC53E5CBJIE {
      background: #222;
   }

   .GC53E5CBBRE,
   .GC53E5CBGRE,
   .GC53E5CBDP,
   .GC53E5CBFQ,
   .GC53E5CBIQ,
   .GC53E5CBFF {
      background: #333;
      color: #ddd;
   }

   .GC53E5CBKP,
   .GC53E5CBJEE {
      background: -webkit-linear-gradient(bottom, #333 0%, #444 60%);
      color: #eee;
   }

   .GC53E5CBGRE a,
   a:visited {
      color: var(--link);
   }

   .GC53E5CBB4D,
   .GC53E5CBOYD {
      color: #eee;
   }

   /*EC2*/
   .awsui {
      background: #222;
      color: #eee;
   }

   .awsui .AP,
   .awsui .AP:active,
   .awsui .AP:focus {
      color: #bbb;
   }

   .CP {
      color: #fff;
   }

   .GIQKP1XDNB .gwt-TabLayoutPanelTab,
   .GIQKP1XL2D,
   .GIQKP1XPUI,
   .GIQKP1XJCC th,
   .GIQKP1XKCC {
      background: -webkit-linear-gradient(top, #333 0%, #444 90%);
      color: #ddd;
   }



   .GIQKP1XIKB,
   .GIQKP1XLTD,
   .GIQKP1XDNB .gwt-TabLayoutPanelTab:hover,
   .GIQKP1XDNB .gwt-TabLayoutPanelTab-selected {
      background: -webkit-linear-gradient(bottom, #333 0%, #444 60%);
      color: #eee;
   }

   .GIQKP1XBPB {
      background-color: #444;
   }

   .GIQKP1XCPB,
   .awsui-icon,
   .awsui-icon-small,
   .external-link {
      filter: brightness(200%);
   }

   .GIQKP1XFLB,
   .GIQKP1XPJB,
   .GIQKP1XGLB,
   .GIQKP1XAKB,
   .GIQKP1XPFI,
   .awsui .awsui-alert-type-info {
      background: #333;
      color: #ddd;
   }

   .GIQKP1XIXH,
   .GIQKP1XDVI {
      color: #eee;
   }

   .GIQKP1XKXH {
      color: #bbb;
   }

   div.tickLabel {
      color: #ddd;
   }

   .GIQKP1XDGI {
      color: var(--link);
   }

   .GIQKP1XCXH {
      border-color: #555;
      border-radius: 2px;
      border-width: 2px;
   }

   .GIQKP1XGKB,
   .GIQKP1XHLB {
      background: #333;
   }

   .GIQKP1XGKB,
   .GIQKP1XKLB,
   .GIQKP1XPKB {
      background: #333;
   }

   .GIQKP1XLLB,
   .GIQKP1XFKB {
      background: #444;
      color: #eee;
   }

   .GIQKP1XNKB {
      background: #555;
   }

   .GIQKP1XC3D {
      background: -webkit-linear-gradient(top, #333 0%, #444 90%);
   }

   .GIQKP1XGPB {
      background-color: #444;
   }

   .GIQKP1XHPB {
      filter: brightness(200%);
   }

   .GIQKP1XCDB,
   .GIQKP1XHDB,
   .GIQKP1XFDB,
   .GIQKP1XDDB {
      filter: brightness(500%);
   }

   .GIQKP1XINB .gwt-TabLayoutPanelTab {
      background: -webkit-linear-gradient(top, #333 0%, #444 90%);
   }

   .GIQKP1XINB .gwt-TabLayoutPanelTab-selected {
      background: -webkit-linear-gradient(bottom, #333 0%, #444 60%);
   }

   /*S3*/
   .sidebar-navigation-container .sidebar-nav-menu .sidebar-nav-menu__link {
      color: #bbb;
   }

   .action-strip {
      background-color: #222;
   }

   .public-text {
      background-color: #865700;
   }

   .counter-literal,
   .columbia-table table a {
      color: #eee;
   }

   .action-strip awsui-button.bucket-action-strip-button .awsui-button.awsui-button-variant-normal.awsui-button-disabled,
   .search-box,
   .awsui .awsui-select-target {
      background-color: #333;
      color: #eee;
   }

   .awsui .awsui-button.awsui-button-variant-normal {
      background: #333;
   }

   .columbia-table table tbody a {
      color: #ddd;
   }

   .columbia-table table tbody {
      background: #333;
   }

   .columbia-table table thead {
      background: -webkit-linear-gradient(top, #333 0%, #444 90%);
      color: #ddd;
   }

   .row-hover {
      background: #444;
      color: #eee;
   }

   .row-hover,
   .row-hover a.list-view-item-name {
      color: #eee;
   }

   .awsui .awsui-tabs-variant-bordered .awsui-tabs-panel,
   awsui-tabs-variant-bordered,
   .header-section,
   .pages-row {
      background-color: #222;
   }

   .awsui .awsui-tabs-tab-active,
   .awsui .awsui-tabs-tab-active:focus,
   .awsui .awsui-tabs-tab-active:hover {
      background-color: #333;
      color: #eee;
   }

   .awsui .awsui-button-dropdown-item-disabled {
      background: #444 !important;
      color: #777;
   }

   .awsui .awsui-button-dropdown {
      box-shadow: 2px 2px 4px #222;
   }

   /*.awsui .awsui-button-dropdown-category .awsui-button-dropdown-item-content {
      background: #333;
   }*/
   .awsui .awsui-button-dropdown-content li {
      background: #333;
   }

   .awsui .awsui-button-dropdown-item a.awsui-button-dropdown-item-focus,
   .awsui .awsui-button-dropdown-item a:active,
   .awsui .awsui-button-dropdown-item a:focus,
   .awsui .awsui-button-dropdown-item a:hover {}

   .card {
      background: #333 !important;
      color: #ddd;
   }

   .awsui .awsui-tabs-variant-bordered .awsui-tabs-panel,
   .awsui table.tagging-table {
      color: #eee;
   }

   .endpoint-label,
   .target-value,
   .awsui awsui-radio-button,
   .awsui awsui-radio-group {
      color: #ddd;
   }

   /*Billing*/
   .containerHeader--36Ndn {
      background: -webkit-linear-gradient(top, #333 0%, #444 90%) !important;
      color: #eee;
   }

   .awsui awsui-table .awsui-table-header-copy th,
   .awsui awsui-table table th {
      background: -webkit-linear-gradient(bottom, #333 0%, #444 60%);
      color: #ddd;
   }

   .awsui awsui-table .awsui-table-header-copy th .awsui-table-header-content,
   .awsui awsui-table table th .awsui-table-header-content {
      color: #ddd;
   }

   .awsui .awsui-util-container .awsui-util-container-header .awsui-util-container-header-description,
   .awsui .awsui-util-font-size-5,
   .awsui .awsui-util-container .awsui-util-container-header,
   .awsui .awsui-util-container > *,
   .awsui awsui-table .awsui-table-header-copy,
   .awsui awsui-table table {
      color: #ddd;
      background: #333;
   }

   /*Cost Explorer*/
   .ui-nav .ui-nav-content,
   .ui-header {
      background: #222;
   }

   .ui-header-nav a {
      color: #eee;
   }

   .ui-nav img,
   .ui-header-menu-toggle .ce-logo,
   .icon-caret-down:before {
      filter: brightness(200%);
   }

   .awsui .awsui-util-container .awsui-util-container-header h1,
   .awsui .awsui-util-container .awsui-util-container-header h2 {
      background-color: #333;
   }

   .eigKGW.eigKGW.eigKGW {
      background: #333;
   }

   .ui-card {
      background: #333;
      color: #eee;
   }

   .chart-tooltip {
      background: #444;
      color: #fff;
   }

   .report-group-by-container {
      background: -webkit-linear-gradient(bottom, #333 0%, #444 60%);
   }

   .group-by-options-container .option {
      color: var(--link);
   }

   .ui-flashbar-info,
   .ui-flashbar-info a {
      background: #444;
      color: var(--link);
      border-color: var(--link);
   }

   .cost-table th,
   .left-header {
      background: -webkit-linear-gradient(bottom, #333 0%, #444 60%);
   }

   .table-row--odd {
      background: #444;
   }

   .date-range-container .picker-dropdown-container .picker-dropdown.open,
   .date-range-container .picker-dropdown-container .picker-dropdown:hover,
   .date-range-container .picker-dropdown-container .picker-dropdown {
      background: -webkit-linear-gradient(top, #222 0%, #333 25%, #333 75%, #222);
   }

   .date-range-container .picker-dropdown-container .picker-dropdown::after {
      filter: brightness(200%);
   }

   ob-daterangepicker .picker-dropdown-container .picker.open {
      background: #444;
      color: #eee;
   }

   calendar .header .date,
   .calendar .board .cell,
   .calendar .board .day {
      color: #eee;
   }

   .calendar .board .days-of-week {
      color: #eee;
      background: #333;
   }

   calendar .board .day.future.in-range:not(.other-month):not(.disabled) {
      background: #23457e;
   }

   .ui-first-header {
      color: #eee;
   }
}
