css:
@-moz-document domain("bringatrailer.com") {
body > div.site-content {
    background: #111 !important;
}
.auctions-item {
    background: #1f1f1f !important;
}
.auctions-item-meta-blocks {
    background: linear-gradient(hsla(0, 0%, 100%, 0), #1f1f1f, #1f1f1f, #1f1f1f) !important;
}
body {
    color: #eee;
}
h3,
.post-title-link {
    color: #eee !important;
}
a {
    color: #eee !important;
}
.header-announcement {
    background: #0f0f0f;
}
.header-announcement-desktop {
    background: #0f0f0f;
}
.toolbar-container,
.listing-intro {
    background: #111;
}
button,
input,
optgroup,
select,
textarea {
    color: #111 !important;
}
#listing-bid-container,
#listing-reserve-history-container,
.listing-essentials {
    background: #1f1f1f;
}
.widget .current-auction-data-holder {
    background-color: rgba(10, 10, 10, 0.9);
    color: #eee;
}
a.button,
button,
div.button,
input[type=button],
input[type=submit],
.data-value {
    color: #eee !important;
}
.button {
    background: #111 !important;
}
.comment-data.bat_seller_comment .comment-actions,
.comment-data.bat_seller_comment .comment-text,
.comment-data.bat_seller_comment .comment-user {
    background: #283849 !important;
}
.bat-bid-reserve .bid-notification-link,
.bat-bid .bid-notification-link {
    color: #2e7ed0 !important;
}
.comment-data.bat-bid-canceled .comment-text,
.comment-data.bat-bid-reserve .comment-text,
.comment-data.bat-bid .comment-text {
    background: #423845;
}
.featured-listings-outer,
.featured-listing-content {
    background: #111 !important;
}
.featured-listing-meta-value {
    color: #eee;
}
.recent-bids-list-item-active .recent-bids-list-item-datum {
    background: #1f1f1f !important;
}
.comment-data.ckrating_highly_rated .comment-actions, .comment-data.ckrating_highly_rated .comment-text, .comment-data.ckrating_highly_rated .comment-user{
    background: #423845 !important;
}
}
