.mobile-sub-menu,
.mobile-sub-menu li a,
.main_links h2 a,
.contacts,
#graph_contents .weather_title,
.sectionheading_h1,
.sectionheading_keldaleft,
.weather_links,
.weather_links a,
.weather_widget_city,
.weather-widget #info,
.weather-widget .temperature,
