small.copyright,
.weather_vevmynd_item a,
.map-preview em,
.sectionsheading_vevmyndir,
.list-video dt,
.sectionsheading_smallvevmyndir,
.sectionsheading_leinkjur,
.leinkjurportal tbody tr td a,
div[style="width: 660px;float: left;margin-top: 30px;"] p,
div[style="width: 660px;float: left;margin-top: 30px;"] p a,
.sectionsheading_lesteldupost,
.sectionsheading_vestfall,
.vestfalskyrrindi .menu h1,
.vestfalskyrrindi .menu .months li,
.kyrrindi,
.kyrrindi caption,
.kyrrindi th,
.center.no-scroll address,
