.articleholder660 .articleauthordate,
#content,
.full-width.shadow.mobile-table tbody tr td,
.soccer_warnings, 
.weather_warnings,
.weather_warnings h2 span {
        color: white !important;
}

.articleholder660 .articleauthordate {
	clear: both;
}

#search-content ul li ul li h2 a {
	color: #f58d7e !important;
}

.leinkjurportal tbody tr td a,
.serach-resuls .tabs a {
	text-decoration: none;
}

.row_info .column_2 table tbody tr:nth-child(2n),
.vestfalskyrrindi .menu .months li.active,
.vestfalskyrrindi .menu .months li:hover {
	background-color: #787878;
}

.mobile-sub-menu li a:hover,
.leinkjurportal tbody tr td a:hover,
.serach-resuls .tabs a:hover {
	text-decoration: underline;
}

#top_vev,
.vev.full-width-center.all {
	display: none !important;
}

.main_content .article .shadow.most-read div div,
.mobile-search form input,
.mobile-search form button,
#search input {
	background: rgba(47, 49, 49, .85) !important;
}

#search button {
	background-color: black !important;
	transition: all .2s;
}

#search button:hover {
	background-color: #555 !important;
}

.mobile-search form button {
	border-left: 1px solid black;
	transition: all .2s;
}

.mobile-search form button:hover {
	background: rgba(50, 50, 50, .5);
}

.weather_info.shadow tbody tr td:last-of-type,
.site_source.dagur a img,
.site_source.fiskur a img,
.contacts img,
.start_mobile_menu img,
.mobile-search img,
.sectionheading_keldaright img,
.weather-widget ul li:last-of-type img,
.row_info .column_2 table tbody tr td:nth-of-type(5) img,
.full-width.shadow.mobile-table tbody tr td:nth-of-type(6),
#graph_contents .page_wrap .row_info .column_2 .h_feed_with_logo,
.emailitem.ivevo,
#search-content ul li ul li span img,
body.antialiased.font-sans,
.weather_warnings h2 a img {
	filter: invert(100%);
}

.weather_info.shadow tbody tr td:nth-of-type(4)[style],
.full-width.shadow.mobile-table tbody tr td:nth-of-type(4),
#category-tag {
	color: #66A2D2 !important;
}

.weather_info.shadow tbody tr td:nth-of-type(3)[style],
.full-width.shadow.mobile-table tbody tr td:nth-of-type(3) {
	color: #FF7F7F !important;
}

/* I'm unsure how to style otherwise */
.flot-base {
	background-color: #EFEFEF !important;
}

/* SVG STYLING */
#forecast_hour_by_hour svg,
#forecast_periods svg,
#forecast_hour_by_hour_second,
#forecast_hour_by_hour_third {
	background-color: #AAA;
}

#forecast_hour_by_hour svg text[x="970"],
#forecast_periods svg text[x="970"],
#forecast_hour_by_hour svg text[x="486"]/*This is probably not best, but so far it works fine*/
,
#forecast_hour_by_hour_second svg text[x="486"],
#forecast_hour_by_hour_third svg text[x="486"] {
	fill: black;
}

#forecast_hour_by_hour svg path[stroke="#89d675"],
#forecast_periods svg path[stroke="#89d675"],
#forecast_hour_by_hour_second svg path[stroke="#89d675"],
#forecast_hour_by_hour_third svg path[stroke="#89d675"] {
	stroke: black;
}
}
