css:
@-moz-document domain("rantingly.com") {
body {
	background: #555
}

div#wrapper {
	background: #bbbccc !important
		/*light gray causes light font on hover*/
}

#logo-or-name img {
	opacity: .7;
	border-radius: 20px
}

li a.headline-link:hover {
	background: none !important;
	color: black
}
/*red important links*/
a[style^="color:#ff3a3a;"] {
	color: #b60247 !important
}

/*blue video links*/
a[style="color:#282fff;"],
a[style="color:#283eff;"]
{
	color: #0b7f6c !important
}

h2{
	text-decoration: underline !important
}
}

@-moz-document domain("63red.com") {
body div.container div {
	background: #999 
}

a.headline:hover,
a.snapHeadline:hover {
	text-decoration: underline !important;
}

img.topLogo {
	opacity: .6;
	border-radius: 20px
}
}
