css:
@-moz-document domain("haxball.com") {
.rightbar {
    display: none;
}
.header {
    display: none;
}
.game-state-view .bar {
    background: transparent !important;
}
.teamicon {
    display: none;
}
.game-view>.bottom-section {
    flex-shrink: 0;
    display: flex;
    justify-content: center;
    background-color: #3c312b;
}
.stats-view {
    background-color: #3c312b;
    padding: 5px;
    margin: 0 5px;
    white-space: nowrap;
    flex: 0 0 100px;
    overflow: hidden;
    line-height: 1.5em;
}
.chatbox-view>.input input[type="text"] {
    color: white;
    background-color: #19111685;
    border: none;
    border-radius: 5px;
    padding: 0 10px;
    margin-right: 4px;
    flex-grow: 1;
    align-self: stretch;
    box-sizing: border-box;
}
.chatbox-view>.log p.notice {
    color: #f9817f;
}
.game-view>.bottom-section button {
    background-color: #29201f;
}
.dialog, .room-view>.container {
    background-color: #3c312b;
    display: flex;
    flex-direction: column;
    align-items: stretch;
    padding: 10px;
    overflow: hidden;
    box-sizing: border-box;
    border-radius: 5px;
}
.dialog button, .room-view>.container button {
    background-color: #24496700;
}
.settings-view .section .inputrow:nth-child(odd) {
    background-color: #222c3100;
}
.settings-view .section select {
    height: 25px;
    border: none;
    background-color: #19111685;
    margin-left: 10px;
}
.settings-view .section .inputrow>:not(:first-child) {
    background-color: #19111685;
    border-radius: 7px;
    margin: 2px 2px;
    padding: 0px 5px;
    display: inline-block;
}
.room-view>.container>.teams .player-list-view .list {
    flex: 1;
    background-color: #19111685;
    overflow-x: hidden;
    overflow-y: auto;
}
.dialog select, .room-view>.container select {
    border: 1px solid #19111685;
    background-color: #19111685;
    border-radius: 5px;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    color: white;
    padding: 0px 5px;
}
.dialog .label-input, .room-view>.container .label-input {
    display: flex;
    background-color: #24496700;
    align-items: baseline;
    border-radius: 5px;
    padding: 3px 3px 3px 5px;
}
.dialog input, .room-view>.container input {
    color: white;
    background-color: #19111685;
    border: none;
    border-radius: 5px;
    padding: 0 10px;
    box-sizing: border-box;
    border: 1px solid #29201f;
}
.dialog>h1, .room-view>.container>h1 {
    font-size: 20px;
    font-weight: 600;
    margin-bottom: 20px !important;
    padding-top: 6px;
    padding-bottom: 5px;
    border-bottom: 3px;
    border-bottom-color: #f98280;
    border-bottom-style: solid;
}
.landing-page .about-box {
    background: rgba(20, 30, 53, 0);
    padding: 20px 55px;
    padding-top: 30px;
    border-radius: 50px;
    margin-top: 40px;
    margin-bottom: 25px;
    text-align: center;
}
.dialog button, .room-view>.container button {
    background-color: #29201f;
}
.dialog .file-btn label, .room-view>.container .file-btn label {
    box-sizing: border-box;
    width: 100%;
    text-align: center;
    background-color: #29201f;
    display: inline-block;
    cursor: pointer;
}
.roomlist-view>.dialog>.splitter .filters .bool, .room-view.roomlist-view>.container>.splitter .filters .bool {
    margin-right: 6px;
    padding: 0px 6px;
    background-color: #29201f;
    border-radius: 5px;
}
.dialog select, .room-view>.container select {
    border: 1px solid #19111600;
    background-color: #29201f;
    border-radius: 5px;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    color: white;
    padding: 0px 5px;
}
.ps__thumb-y {
    background-color: #29201f;
    border-radius: 6px;
    transition: background-color .2s linear, width .2s ease-in-out;
    -webkit-transition: background-color .2s linear, width .2s ease-in-out;
    width: 6px;
    right: 2px;
    position: absolute;
}
.roomlist-view>.dialog>.splitter>.list .separator, .room-view.roomlist-view>.container>.splitter>.list .separator {
    height: 2px;
    width: 100%;
    background-color: #f98280;
}
.game-state-view.restricted {
    background-color: #3c312b;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}
.landing-page .about-box .play-link {
    margin-left: auto;
    margin-right: auto;
    margin-top: 20px;
    margin-bottom: -40px;
    background: linear-gradient(#3c312b, #3c312bbf);
    border-radius: 200px;
    padding: 15px;
    font-size: 35px;
    font-weight: bold;
    display: block;
    text-align: center;
    width: 237px;
    color: white;
    border: 3px solid rgba(247, 255, 235, 0.7);
    box-shadow: 0 0 0 3px #8da86b, 0 0 6px 6px rgba(0, 0, 0, 0.33);
    text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.5);
    transition: 0.1s linear
}
.replay-controls-view {
    -webkit-user-select: none;
    user-select: none;
    position: absolute;
    bottom: 0;
    height: 40px;
    width: 100%;
    background-color: transparent;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    padding-left: 6px;
    padding-right: 6px;
    box-sizing: border-box;
}
.replay-controls-view button {
    background-color: #29201f;
}
.replay-controls-view .timebar .bar {
    background-color: #29201f;
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
}
.rc-anchor-dark {
    background: #3c312b;
    color: #fff;
}

.settings-view .tabs .selected {
    background-color: #f98280;
}
}
