css:
@-moz-document domain("render.figure-eight.io") {
/*
Figure Eight Navy Blue
Author: Manuel Uribe
Facebook URL: fb.com/mnluribe
Description: A style to change the white background of the Figure Eight Task list to a sort of night (blue) mode.
Donation: https://paypal.me/mnluribef
Version: v1.1
*/

h1, .h1 {
    color:#928484;
}

div#content{
    background-color: #152439 !important;
    background: none;
}

th{
    color: #152439;
}

.new_exit_survey{
    background-color: inherit;
}

.form-actions{
    background-color: #152439;
}

div.missed{
    color: white !important;
}

#footer{
    background-image: none;
}
}

@-moz-document domain("tasks.figure-eight.work") {
h1, .h1 {
    color:#928484;
}

body{
    background-color: #152439;
    color:white;
}

.table > thead > tr > th {
    background-color: #4e5d74;
    color: white;
}

.well{
    background-color: #4e5d74;
}

.table-striped > tbody > tr:nth-child(odd) > td, .table-striped > tbody > tr:nth-child(odd) > th {
    background-color: #2a405efa;
}

.table-striped > tbody > tr:nth-child(even) > td, .table-striped > tbody > tr:nth-child(even) > th{
    background-color: #2a405efa;
}

.btn-default:hover, .btn-default.hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open > .dropdown-toggle.btn-default {
    background-color: #4e5d74;
}

.btn-default.btn, .btn-default.btn-primary, .btn-default.btn-secondary{
    background-color: #4e5d74;
    color: white;
}

div.task_listing table.task-listing td{
    border-top: 2px solid #4e5d74;
    border-bottom: 2px solid #4e5d74;
}

a{
    color: #0e9bc9 !important;
}

p{
    color: #b5a1a1;
}

div#content{
    background-color: #152439 !important;
    background: none;
}

.hero-unit{
    background-color: #4e5d74;
}

.hero-unit h1{
    color: white;
}

#footer{
    background-image: none;
}

#job-history .table-legend{
    color: white;
}

input:not([type="checkbox"]):not([type="radio"]):not([type="file"]):not([type="submit"]){
    color: black;
    height: 25px;
}
}

@-moz-document url-prefix("https://contributors.figure-eight.work/") {
body{
    background-color: #152439;
}
}
