css:
@-moz-document domain("grough.co.uk") {
 @media only screen and (max-width: 600px) {
     #mai-rtmenu {
         display: none;
     }

     #header {
         display: none;
     }

     #main #mai-container #mai-content {
         width: 100%;
         background: none;
     }

     #main #mai-container {
         width: 100%;
         background: none;
         border-left: none;
     }

     #footer {
         display: none;
     }

     .article-comments {
         display: none;
     }

     body {
         background: #333333;
         color: #ffffff;
     }

     #main {
         background: inherit;
     }

     .article-container h1 {
         color: inherit;
     }

     #mai-navpath {
         display: none;
     }

     .byline {
         display: none;
     }

     .options {
         display: none;
     }

     .article-body .alignleft {
         float: inherit;
         margin-left: auto;
         margin-right: auto;
         padding: 20px;
     }

     .article-body .alignright {
         float: inherit;
         margin-left: auto;
         margin-right: auto;
         padding: 20px;
     }

     .article-related {
         display: none;
     }
 }
}
