css:
@-moz-document domain(dropbox.com) {

/*
	Dropbox Paper Sidebar
  	Author: Masaki Kobayashi
	Contact: m.kobayashi.me@gmail.com
	License: Public Domain
	Created: Sep. 04, 2019
*/
  
  @media screen and (min-width: 1024px) {
      .bottom-insert-toolbar-wrapper {
          top: calc(50% + 31px);
          bottom: auto;
          left: auto;
          right: 50px;
      }   

      .insert-toolbar-overlay {
          display: flex;
          flex-direction: column;
          align-items: center;
      }

      .bottom-insert-toolbar-wrapper .insert-toolbar>.insert-toolbar-overlay>.hpx-button {
          display: block;
          margin-right: 0;
          margin-bottom: 5px;
      }

      .bottom-insert-toolbar-separator {
          border: none;
          display: block;
          width: 24px;
          height: 1px;
          margin: 0 0 5px;
          background-color: #000;
      }
  }

}
