css:
@-moz-document url-prefix("https://www.stine.uni-hamburg.de") {
* {
    font-family: 'Roboto', sans-serif !important;
}


/* Top Bar */
#pageHeadBottom_1 {
    display: none;
    background-color: #027bcb;
}
#pageHeadBottom_2 {

    background-color: #027bcb;
    border: none;
}
#pageHeadBottom_3 {
    position: relative !important;
    top: -40.5px !important;
    background-color: #027bcb00 !important;
    border: 0px;
    width: 150px;
    margin-left: auto;
    margin-right: 13px;
}
#pageTopNavi ul.nav {
    background-color: #027bcb;
}

a.depth_1:hover {
    background-color: #fff !important;
    color: #027bcb !important;
}
a.depth_1 {
    background-color: #027bcb !important;
    padding: 1em !important;
    font-size: 1.5em;
    text-transform: uppercase;
    color: #fff !important;
}
li.depth_1 {
    margin: 0 !important;
}
#extraNav_link4 {
    display: none;
}
#extraNav_link5 {
    display: none;
}
.img.img_LangEnglish {
    background-image: none;
    color: #fff !important;
    margin-top: 0px !important;
}
.img.img_LangEnglish:hover {
    color: #fff;
}
.img.img_LangEnglish:visited {
    color: #fff;
}
.img.img_LangGerman {
    background-image: none;
    color: #fff;
    margin-top: 0px !important;
}
.img.img_LangGerman:hover {
    color: #fff;
}
.img.img_LangGerman:visited {
    color: #fff;
}

#pageTopNavi {
    width: auto !important;
    position: static;
}

ul.depth_2 {
    margin-top: 121px !important;
}

#pageHeadBottom_2 {
    display: none;
}

#logoutButton,
#logIn_btn,
#forgotPassword_btn {
    background-color: #0000 !important;
    border: 0px !important;
    color: #fff !important;
    position: static !important;
    line-height: 22px !important;
    margin: 0 !important;
}
#logoutButton.img,
#forgotPassword_btn.img {
    background-color: #0000 !important;
    border: 0px !important;
    color: #fff !important;
}
#logIn_btn.img,
#forgotPassword_btn.img {
    background-color: #0000 !important;
    border: 0px !important;
    color: #000 !important;
}
#logIn_btn,
#forgotPassword_btn {
    background-color: #0000 !important;
    border: 0px !important;
    color: #fff !important;
    position: static !important;
    line-height: 22px !important;
    margin: 0 !important;
    margin-top: 2px !important;
}

/* Side Bar */
#pageTopNavi ul.nav li.depth_2 a {
    font-weight: bold !important;
    background-image: none !important;
    border: 0px !important;
    padding-top: 15px;
    padding-bottom: 15px;
    font-size: 1.2em;
}

#pageTopNavi ul.nav li.depth_2 {
    border: 0px !important;
}

#pageTopNavi ul.nav.depth_2 {
    border-right: 1px solid #e0e0e0 !important;
}

#pageTopNavi ul.nav li.depth_3 a {
    font-weight: normal !important;
}

#pageTopNavi ul.nav li.depth_3 {
    padding-top: 7px;
    padding-bottom: 7px;
    border: 0px !important;
}

#pageTopNavi ul.nav li.depth_3:hover {
    padding-left: 3px !important;
    border-left: 5px solid #027bcb !important;
    background-color: #fff;
}

#pageTopNavi ul.nav li.depth_3 a {
    font-size: 1em;
    padding: 4px 4px 4px 17px;
}

#pageTopNavi ul.nav.depth_2 li.depth_2 * a:hover {
    color: #000 !important;
}

#pageLeftTop,
#pageLeft {
    min-height: 0px;
    height: 1px;
}

/* Content */
#pageContentContainer {
    top: -28px;
    position: relative;
}

#pageContent {
    min-height: 800px;
}


h1 {
    font-size: 2em !important;
    color: #e2001a !important;
    text-transform: uppercase;
}

/* Table */
.tb, .tb750{
    border: 0 !important;
}

.tbhead, caption {
    padding: 1em 0 1em 0;
    color: #000;
    font-size: 16px;
    background-color: #0000;
}

.tbsubhead th, .tbsubhead{
    background-color: #fff;
    color: #000;
    padding: 0.75em 0 0.75em 0;
}

tr.subhead td,
.subhead td,
.tbsubhead,
.subhead {
    background-color: #0000 !important;
    color: #000 !important;
    border: 0;
}

a {
    color: #000;
}

tbody {
    border-top: 1px solid !important;
}

td.level01 {
    background-color: #e2001a1f;
    padding-left: 4px !important;
}

tr {
    padding: 0.5em 0 0.5em 0 !important;
}

.img_arrowReload {
    background-image: none !important;
    padding-left: 1em !important;
}

/* Footer Bar */
#pageFootControlsRight {
    display: none;
}

#pageFootControl_up {
    display: none;
}

#pageFootControlsLeft {
    margin-left: auto !important;
    margin-right: 10px;
    float: right;
}

#pageFoot {
    background-color: #000;
    border: 0px;
    height: 22px;
    bottom: 0px;
    right: 0px;
    left: 0px;
    position: fixed;
}


/* Fix page Size */
html {
    min-width: 1650px;
}

