input, select, a.button, a.submit, a.BigButton, a.TabLink, .install[onclick] {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    transition: border-color 0.3s !important;
    color: #BBB !important;
    text-shadow: 0 1px #000 !important;
    background: #060606 !important;
    border-radius: 4px !important;
    box-shadow: 0 0 2px rgba(0,0,0,.9) !important}

html, body, #nav-view-responsive {
    
    background:#1c1c1c !important;
    background-image: none 
}

 * {
    border-color:#444444 !important;
     color: #ffffff 
}

#sidebar_header {
     color:#0a0a0a !important;
    background:#242424 !important;
}


 a {
    
    text-decoration:none !important;
    text-shadow:none;
    transition:all .6s;
}


 #sidebar_container  {
    color:#0a0a0a !important;
    background:#303030 !important;
}



.btn-link {
    font-weight: 400 !important;
    color: #007bff!important;
    background-color: transparent !important;
    border-color: none !important;
}

.alert-success {
    color: #155724 ;
    background-color: #00c23a;
    border-color: #c3e6cb;
}
.alert-warning {
    color: #856404;
    background-color: #f9de88;
    border-color: #ffeeba;
}
.alert-danger {
    color: #721c24;
    background-color: #fd5261;
    border-color: #f5c6cb;
}
.alert-primary {
    color: #004085;
    background-color: #5185bb;
    border-color: #b8daff;
}
.alert-secondary {
    color: #383d41;
    background-color: #b5b5b5;
    border-color: #d6d8db;
}
.alert-light {
    color: #818182;
    background-color: #dedede;
    border-color: #fdfdfe;
}
.btn-outline-primary {
    color: #007bff  ;
    background-color: transparent  !important;
    background-image: none  !important;
    border-color: #007bff  !important;
}

.btn-outline-info {
    color: #17a2b8 ;
    border-color: #17a2b8 !important;
}

.btn-outline-danger {
    color: #dc3545 ;
    background-image: none !important;
    border-color: #dc3545 !important;
}

.swal2-popup {
    display: none;
    position: relative;
    box-sizing: border-box;
    flex-direction: column;
    justify-content: center;
    width: 32em;
    max-width: 100%;
    padding: 1.25em;
    border: none;
    border-radius: .3125em;
    background: #1d1d1d;
    font-family: inherit;
    font-size: 1rem;
}

.swal2-icon.swal2-info {
    border-color: #9de0f6 !important;

}

.header1 {
    font-family: "Roboto";
    font-weight: bold;
    color: white;
    font-size: 1.5rem;
}

element.style {
    width: 40rem!important;
    padding: 1rem!important;
    background: rgb(37, 37, 37)!important;
    display: flex!important;
}

.swal2-container:not(.swal2-top):not(.swal2-top-start):not(.swal2-top-end):not(.swal2-top-left):not(.swal2-top-right):not(.swal2-center-start):not(.swal2-center-end):not(.swal2-center-left):not(.swal2-center-right):not(.swal2-bottom):not(.swal2-bottom-start):not(.swal2-bottom-end):not(.swal2-bottom-left):not(.swal2-bottom-right):not(.swal2-grow-fullscreen)>.swal2-modal { 
        background: rgb(21, 21, 21)!important;
}

.list-group-item {
    background-color: #2b2b2b 
}
}
