css:
@-moz-document domain("boardgamegeek.com") {

body {
    font-family: 'Open Sans', proxima-nova, Arial, sans-serif;
}

.container-fluid {
    max-width:initial;
}

gg-skyscraper-ad {
    display:none;
}

.quickbar-list-group {
    margin-bottom: -5px;
}

.geekcentral-controls {
    display: none;
}

/*
.legacy .commands li a {
    color: white;
}
*/

.legacy .commands li {
    margin: 0 4px !important;
}

gg-header header, .global-header, .media-header, .legacy .moduletable th, .legacy .menu_table, .legacy .tabOn, .global-secondary-nav-section-header, .btn-primary, .modal-header, .geekcollection_pager, #blog_dashboard_commands h2, #blog_title, .profile_table th, .legacy div.recommendbox_str, #blog_header, #blog_nav h2, .legacy .quotetitle, .legacy .forum_table th {
    background-color: #306090 !important;
}

.home-tab__inner-container {
    display: none;
}

geekoutput-footer, gg-footer {
    display: none;
}

footer.mt-5 {
    margin-top: 0 !important;
}

.footer__container {
    display: none;
}

.global-header-logo, .menu-logo-inner {
    display: flex;
    align-items: center;
}

gg-menu-nav-user span.count, a.global-header-icon span.badge, button.btn.global-header-icon span.badge {
    background-color: #f07000 !important;
}

.menu-logo-inner > a:hover {
    text-decoration: none;
}

.global-header-logo > a > img, .menu-logo-inner > a > img {
    display: none;
}

.global-header-logo > a:after, .menu-logo-inner > a:after {
    content: "BGG";
    color: orange;
    font-size: 24px;
    font-weight: 800;
}

.media-filters {
    display: none;
    background-color: #4070a0 !important;
}

.nav-tabs-more-dropdown a:visited, .summary-image-item .summary-item-title a:visited, .panel-body-toolbar > a:visited, .advertisement-text-body a:visited, ul.global-secondary-nav-links a:visited, .link-text-color:visited {
    color: #4080c0;
}

.homepage-container.global-body-content-primary {
    background-color: transparent;
}

.homepage-container {
    padding-top: 0 !important;
}

.geekcentral {
    padding: 0;
    background-color: transparent;
}

.media-module__body {
    background-color: white;
}

.media-cell {
    display: flex;
    align-items: center;
    padding: 4px 8px;
}

.media-cell > h2 {
    flex: 1 1 auto;
    margin-right: 8px;
}

.media-cell > p {
    flex: 0 0 auto;
    margin: 0;
}

.media-cell:hover {
    background-color: #f0f0ff;
}

.media-cell:hover h2 > a {
    text-decoration: none;
}

/*.media-cell > p > span:not(:first-child) {
    display: none;
}

.media-cell > p > span::after {
    display: none !important;
}*/

.legacy {
    padding: 0;
    background-color: transparent;
}

.legacy .innermoduletable th {
    color: white;
}

.legacy .tabOn, .legacy .tabOff {
    border: none;
}

#container {
    margin-right: 0 !important;
    margin-left: 0 !important;
}

.moduletable {
    border: none !important;
    box-shadow: 0 0 2px 0px rgba(0, 0, 0, 0.3) !important;
}

.global-secondary-nav-section, .media-module {
    box-shadow: 0 0 2px 0px rgba(0, 0, 0, 0.3) !important;
}

.table-responsive {
    overflow-x: initial;
}
  
}
