css:
@-moz-document domain(fcupdate.nl){

    #content-wrap{
        background-color: rgb(40,40,40) !important;
    }

    article{
        background-color: rgb(70,70,70) !important;
        color: #CCCCCC !important;

    }

    a{
        color: #CCCCCC !important;
    }

    h1{
        color: #CCCCCC !important;
        background: rgb(70,70,70,0.8) !important;

    }

    p{
        color: #CCCCCC !important;
    }

    div.top-articles li{
        background-color: rgb(90,90,90) !important;
    }

    ol{
        background-color: rgb(70,70,70) !important;
    }

    div.top-articles{
        background-color: rgb(90,90,90) !important;
        border-color: rgb(80,80,80) !important;
        box-shadow: 0 5px rgb(70,70,70) !important;
    }

    div.title{
        color: #CCCCCC !important;
    }

    div.main-navigation{
        background-color: rgb(70,70,70) !important;
        border-color: transparent !important
    }

    nav.top{
        border-color: rgb(40,40,40) !important;
    }

    img{
        border-color: transparent !important;
    }

    h3{
        background-color: rgb(80, 80, 80) !important;
        color: #CCCCCC !important;
    }

    div.textual.full.active{
        background-color: rgb(80,80,80) !important;
    }

    div.icon.full.active{
        background-color: rgb(80,80,80) !important;
    }

    footer{
        background-color: rgb(70,70,70) !important;
    }

    div#more-news-button{
        background: none !important;
        background-color: rgb(70,70,70) !important;
    }

    li.tab{
        background-color: rgb(40,40,40) !important;
    }

    .even, tr.even td{
        background-color: rgb(70,70,70) !important;
    }

    .odd, tr.odd td{
        background-color: rgb(90,90,90) !important;
    }

    th{
        background-color: rgb(60,60,60) !important;
        color: #CCCCCC !important;
    }

    div.bottom{
        background-color: rgb(60,60,60) !important;
    }

    h4{
        background-color: rgb(50,50,50) !important
    }

    scrollable{
        background-color: black !important;
    }

    td{
        color: #CCCCCC !important;
    }

    div{
        color: #CCCCCC !important
    }

    div.smart{
        background-color: rgb(70,70,70) !important;
    }

    div.list{
        background-color: rgb(70,70,70) !important;
    }

    nav.top li, .main-navigation{
        border-color: #CCCCCC !important;
    }

    h4 a{
        background-color: transparent !important;
    }

    div.contentBlock{
        background-color: rgb(70,70,70) !important
    }

    div#mc_embed_signup{
        background-color: rgb(70,70,70) !important
    }

    input#mce-EMAIL.email{
        background-color: rgb(90,90,90) !important;
        color: #CCCCCC !important;
    }

    div.photolist div.container{
        background-color: rgb(70,70,70) !important
    }

    div.EmbeddedTweet-tweet{
        background-color: rgb(90,90,90) !important;
        color: #CCCCCC !important;
    }

    td{
        background-color: rgb(50,50,50) !important
    }

    ul{
        background-color: rgb(70,70,70) !important;
            border: rgb(70,70,70) !important;
    }

    .livereport-message-list .message:nth-child(even) {
    background: rgb(102, 102, 102);
    }
}
