@-moz-document regexp('http://estacio.webaula.com.br/Classroom.*?'), regexp('http://simulado.estacio.br/.*?') {
  #geral > tbody > tr > td > table {
    margin-bottom: 20px;
  }

  #geral > tbody > tr > td > table > tbody > tr > td {
    padding: 0 8px 15px;
  }

  td.linha_text_med_000000,
  font {
    font-size: 16px;
    line-height: 1.6;
    font-family: 'Noto Serif', serif;
  }

  .tabela_redonda {
    max-width: 1400px !important; 
  }

  .corpo_main {
    zoom: 130% !important;
  }
}
