css:
@-moz-document domain("echoprzestrzeni.pl") {
.post-body, .entry-content > span {
    color: #3d85c6 !important;
    font-family: courier;
}

* {
    text-align: left !important;
}

.content-outer, .descriptionwrapper, .content-inner, .main-inner, .titlewrapper {
    padding: 0 0 0 !important;
    padding-top: 0 !important;
    margin-bottom: 0 !important;
}

footer, .comments, .navbar, .body-fauxcolumns, .feed-links, .post-footer-line, .post-comment-link {
    display: none;
}

a {
    color: #ff9900;
}

h3.post-title, .entry-title {
    font: normal bold 24px'Trebuchet MS', Trebuchet, sans-serif !important;
    color: #ff9900 !important;
    margin-top: 0 !important;
    text-decoration: underline overline;
}

.post-title.entry-title {
    margin-top: 15px !important;
}

.blog-pager-older-link, .blog-pager-newer-link, .home-link {
    font: normal bold 12px'Trebuchet MS', Trebuchet, sans-serif !important;
    color: #ff9900 !important;
    margin-top: 0 !important;
    text-decoration: underline overline !important;
}

body {
    background: #000 !important;
/*    background-image: none !important;    
    background-image: url(http://thewingedhussars.com/images/bg_start.jpg) !important; */
    background-image: url(https://forum.thewingedhussars.com/images/TWH-ANUKUS/bg_space.jpg) !important;
/*    background-repeat: no-repeat !important; */
    background-size: auto;
    background-attachment: fixed !important;
    font-size: 19px !important;
}

.home-link {
 	position: relative;  
    left: 40%;
}

.date-outer {
    border: 2px solid #f90;
	border-radius: 8px;
	background-color: #1a1a1a;
}

.date-header {
    text-align: center !important;
/*    margin-top: 5px; */
    margin-bottom: 10px;
    color: #f90 !important;
}

.date-header {
    text-align: center !important;
    margin-top: 0px;
    margin-bottom: 10px;
    color: white !important;
	background-color: #f90;
    padding-top: 10px;
    padding-bottom: 10px;
}

.post-outer {
    padding-left: 15px;
    padding-right: 15px;
}

.jump-link a {
    color: #f90 !important;
    margin-bottom: 25px !important;
    border-bottom-style: solid;
    display: block;
    border-bottom-width: 1px;
    font-size: 12px;
}
}
