css:
@-moz-document url-prefix("https://animevost.org") {
	::-webkit-scrollbar {width: 2px;}
div.headbgnew,div.headbgnewo,div.headbgnewfour,div.headbgnewthree,div.headbgnewtwo,div.headbgnewone{height: 50px;background: none !important;}
div.headbgnew,div.headbgnewo,div.menu,div.headbgnewfour,div.headbgnewthree,div.headbgnewtwo,div.headbgnewone{top: 0px !important;}
body{background: url(/*[[fon_image]]*/);background-repeat:no-repeat;background-attachment:fixed; background-size: cover;overflow-x: hidden}
.topLine .login .loginLink{background: #01186d70!important;padding: 12px 0 11px 0!important;}
.loginLink{border-radius:0px!important;}
.loginAva{background: #01186d70!important;height: 38px;border-radius:0px!important;}
.loginAva img{padding: 2px 2px!important;border-radius:0px!important;}
.login a img{width:36px;height:36px;content:url(data:image/png;base64,);}
.loginLinkZ,.loginLinkS{padding:10px;text-decoration:none!important;}
.loginLinkS {padding-right:10px!important;border-radius:0px!important;}
.loginLinkZ:hover,.loginLinkS:hover{padding:10px;text-decoration:none!important;background:rgba(5,65,68,0.76);}
.search{margin: 0px 0px 0px 0px;width: 300px;text-align: -webkit-center;}
div.topLine div.search{margin: 0px -26% 0px 0px;}
#searchsuggestions{background-color:#fff0;overflow-y:scroll;height:544px!important;border:0px!important;}
#searchsuggestions p {color: #fff!important;}
#searchsuggestions a{background-color:rgba(34,41,90,0.95);border:0px!important;}
#searchsuggestions a:hover{background:rgb(0,0,0);}
.searchheading,.seperator{background:rgb(29,80,104)!important;}
div.topLine div.search form input.search{background:rgb(66,66,66)}
div.topLine div.login form{background:rgb(66,66,66);}
div.topLine div.login form #login_name, div.topLine div.login form #login_password {background: rgb(174, 162, 146);color: #fff!important;}
div.topLine div.login form .buttonEx{background: #aea292 url(https://animevost.org/images/buttonExBG.png) no-repeat center;}
div.topLine div.login span.fromSpan{color: #fff!important;}
div.topLine div.login span a {color: #d7d7d7;}

.menu ul#topnav li span.sar,.menu ul#topnav li .sar{background:rgba(0,0,0,0.75)!important;}
#blok_1,#blok_2,#blok_3,#blok_4,#blok_5,#blok_6,#blok_7,#blok_8,#blok_78,#blok_19,#blok_20{border-radius:0px;background:rgba(0,0,0,0.75)!important;padding: 9px 2px;}
#blok_20{width:100px;}
ul#topnav li span.sar span a,ul#topnav li .sar span a{padding:3px 0px 7px 0px;color:#00cab7;font-size: 16px;text-decoration:none;border:0px;text-align:center;}
ul#topnav li span.sar span a:hover,ul#topnav li .sar span a:hover{background: rgba(93, 125, 125, 0.63);border-radius:6px;}
.menu ul#topnav li .sar {bottom: 0px;top: 45px;z-index:2;}
div.topLine div.login a.loginExit{background: #00000059;}
div.topLine div.login a.loginExit:hover{background: #00000096;}

#wrap{margin: 10px 0px 10px 0px!important;}
div[style="margin: 0 auto; width: 1000px;"]:first-child{width:78.8%!important;margin: unset!important;}
.headbgnew+div{margin:unset!important;width:126%!important}
div.headbgnewo,div.menu {width: 100%!important;top: 281px;background: rgba(0,0,0,0.75)!important;}
#leftBlock{margin-top: 5px!important;width: 22%!important;}
div.imgOngoing img{height: 100%;}
.moduleLeft{width: 100%!important;}
#centerBlock{width: 76%!important;}
.headbgnew+div,.headbgnewo+div,.headbgnewfour+div,.headbgnewthree+div,.headbgnewtwo+div,.headbgnewone+div{margin:unset;width:100%!important;}
.shortstory,.shortstoryHead,.staticInfo{width: 100%!important;}
.imgRadius{width:100%!important}
div.banerTop {width: 100%!important;border: 1px solid #8b8b8b;background: rgba(71, 71, 71, 0.75);}
td div:first-child{width: 26%!important;}
.shortstoryContent {width: 98%;}
.shortstoryContent p,.shortstoryContent p+div{color: #979a9c;}
.shortstoryContent a {color: #2ca4df;}
.skrin img {width: 32.5%;height:100%;}
#player2 {width: 100%;height: 533px;background: #393939;border: 1px solid #ffffff;}
#scrolltwo {width: 93%;}
table {width: 100%!important;}
#anime {width: 99.8%;height: 533px;}
div.imgOngoing a:hover{opacity: 0;}
div.interDubBgTwo h2{background:rgb(31, 31, 34);border-bottom: 1px solid #707070;}
.interDub div.interDubBgTwo {background:#1f1f22;border: 1px solid #6c6c6c;}
div.interDubBgTwo ul li a {border-bottom: 1px solid #fff;color: #cfcfcf;text-shadow: none;font-size: 101%;line-height: 100%;}
div.interDubBgTwo ul li a:hover{background:#000!important;}
div.interDubBgTwo ul li {border-bottom: 1px solid #1e1e1e;}
.raspis .popup {text-shadow: none;border: 1px solid #9c9c9c;background: rgba(3, 3, 3, 0.95);font-weight: bold;color: #a09e9e;}
.nexttime{background: #373737;color: #f2f2f2;}
#nexttime{background: #959594;color: #f2f2f2;}
.interDubBgTwo b{color: #ffffff!important;border-bottom: 1px solid #7b7a7a!important;}
.interDubBgTwo p{color: #ffffff!important;}
.interDubBgTwo a{background: #414141!important;}
#raspisMon a, #raspisTue a, #raspisThu a, #raspisFri a, #raspisSat a, #raspisSun a, #raspisWed a, #raspisNest a{color: #fff;}
#raspisMon a:hover, #raspisTue a:hover, #raspisThu a:hover, #raspisFri a:hover, #raspisSat a:hover, #raspisSun a:hover, #raspisWed a:hover, #raspisNest a:hover{background:rgb(138, 138, 138)!important;}
.imgOngoingVie,div.imgOngoing a,div.imgOngoing span,div.imgOngoing img{width:100%;background: #6c6c6c;}
.shortstory {background: #39393994;}
div.miniInfo{background:rgba(0, 0, 0, 0.76);color:#c5c5c5;}
.shortstory .shortstoryHead h2, .shortstory .shortstoryHead h1{color: #bfbfbf;text-align: center;border-bottom: 1px solid #72716e;text-shadow: none;}
.shortstoryContent h4{background: #959594;text-shadow: 0px 1px 0px rgb(0, 0, 0);}
div.staticInfo{background: #626262;border-bottom: 1px solid #e4ddd4;}
fieldset.skrin {border: 1px solid #c3c3c3;}
fieldset.skrin legend {color: #fff4e8;}
.shortstoryContent #shortstoryContentTegi, #shortstoryContentTegi a,.bottomProblem,.shortstoryFuter,.ca-container{display:none;}
.epizode {background: #384f4f;border-right: 1px solid #c3c3c3;color: #acacac;border-top: none;}
.epizode:hover,.active{background: #1f272f;}
.prev{background:url('data:image/png;base64,')!important;width: 39px!important;height: 38px!important;}
.next{background:url('data:image/png;base64,')!important;}
#stdplayer {border: 1px solid #c3c3c3;}
.functionPanel {height: 20px;border: 1px solid #c3c3c3;color: #938e87;}
.functionPanel div{border-right: 1px solid #c9c7c4;background: none;width:49.8%;background: rgba(0, 0, 0, 0.59);}
.shortstoryContentTegi,.present,#stext{display:none;}
.shortstoryContent{border-bottom: none;}
.shortstory .shortstoryHead h2 a, .shortstory .shortstoryHead h1 a{color: #bfbfbf;}
.shortstoryContent .title_spoiler a{color: #c1c0bf;}
.shortstoryContent a{color: #f6fafc;}
.ui-widget-header{background: rgba(0, 0, 0, 0.82);}
#fastPunkt .one{background: #6c6c6c;border: 1px solid #e1e1e1;color: #f2ece4;}
#fastPunkt .two{border: 1px solid #e1e1e1;background: #6c6c6c;}
.rounded-white{background:none!important;}

.global_search{background: #959594;width: 99%;border: 1px solid #cccccc;}
textarea:focus, input:focus, select:focus{background: antiquewhite;}

td[style='padding: 0px; border: none; margin: 0px; line-height: 0; font-size: 0px; background: url("https://animevost.org/engine/classes/highslide/graphics/outlines/rounded-white.png") 0px -40px; height: 20px; width: 20px;']{background:none!important;}
td[style='padding: 0px; border: none; margin: 0px; line-height: 0; font-size: 0px; background: url("https://animevost.org/engine/classes/highslide/graphics/outlines/rounded-white.png") 0px -60px; height: 20px; width: 20px;']{background:none!important;}
.block_4 span,.block_4 a{padding: 5% 6%;border-radius: 0px;color: aquamarine;}
.block_4 a{background: rgba(0, 0, 0, 0.62);}
.block_4 span,.block_4 a:hover{background: rgba(94, 90, 90, 0.62);}
.block_3{width: 15%;}
.block_4{width: 17%;}
.ui-widget-content{background: #a8a8a8b5 50% bottom repeat-x;}
#anime {background: #4f4f4f;}

#newComment{background: #0202028f;border: 1px solid #9e9e9e;}
#form_comment button{border: 1px solid #ffffff;background-color: #4d676b;color: #ffffff;}
.bb-editor textarea{background: #959ca2;}

#rulesComment{background: rgba(0, 0, 0, 0.63);border: 1px solid #b00000;}
#rulesComment a{color:#f00;}
#rulesComment ul{border-top: 1px solid #a8a8a8;border-bottom: 1px solid #a8a8a8;}
#rulesComment ul li{color:#cfcfcf;}
#rulesComment .form_comment div{color:#919191;}
.commentFinal{border: 1px solid #414141;background: #000000b8;}
.commentFinalText{color: #bababa;background: #0000009e;border-left: 1px solid #a0a0a0;}
.commentFinalAva {border-right: 1px solid #a0a0a0;}
.commentFinalAva span a, .commentFinalAva span{color: #ddc7ac;}
.commentFinalData{border-left: 1px solid #a0a0a0;border-bottom: 1px solid #a0a0a0;color: #f4e1ca;}
.commentFinalIt{border-top: 1px solid #a0a0a0;border-left: 1px solid #a0a0a0;color: #eaccab;}
.scriptcode, .title_quote, .quote, .title_spoiler, .text_spoiler{color: #1ecaca;border: 1px solid #cfcfcf;background: #262626ab;}
.shortstoryContent .title_spoiler a:hover{color: #aaaaaa;}
.title_quote, .title_spoiler{background: #494847;}
#futer{border: 1px solid #a4a4a4;width: 100%;background: rgba(0, 0, 0, 0.38);}
#futer #copyr{width: 60%;}
#futer #fraind{width: 40%;}
#futer #copyr li a{color: #ececec;}
#futer #copyr ul li{color: #958f86;}
#futer #copyr div#copyrText{color: #c3bbbb;width: 96%;}
#copyrText a{color: rgb(255, 14, 14);}
.userinfo{width: 100%;background: #00000091;border: 1px solid #a2a2a2;}
#newComment p, .userinfoHead{color: #fbecd9;width: 99%;border-bottom: 1px solid #626262;}
.userinfoCenter {color: aliceblue;}

.block_10,.block_13,.block_14 select,.bbcodes{background: #80725d!important;color: rgb(216, 216, 216);border: 1px solid #f5f5f5;}
.block_10:focus,.block_13:focus{background: rgb(216, 205, 186) !important;}

}

@-moz-document url-prefix("https://play.animegost.org/") {
#knpki{background: #00000094!important;border-bottom: 1px solid #ffffff!important;}
#knpki div{color: #d1e9ff!important;}
#knpki a{background: #0000008c!important;border: 1px solid #cacaca!important;color: #dadada!important;}
#knpki a:hover{background: #098462!important;}
}
