css:
@-moz-document domain("vp.fo") {
#wrapper {
	width: 0px;
}

#wrapper,
#wrapper #container,
body,
#footer {
	background-color: #333;
	min-width: 980px;
}

#topheader,
#topdato2 {
	margin: 0 auto;
}

#topdato2 {
	min-width: 980px;
}

#wrapper #container #top,
#aduttan {
	display: none;
}

#wrapper #container div,
#tx_indexedsearch #searchField,
.entry .header h3 a span,
#listcategorypostswidget-2 .news ul li a span,
.day .date,
.day .precipitation,
.day .windtext,
.day .temperatures .temperatureUnit,
.day .temperatures .tempsep {
	color: white;
}

.oneLocationForecast3-wrapper {
	display: flex;
}

.day {
	box-shadow: none !important;
	border: none !important;
}

.day:first-of-type {
	order: 0;
}

.day:nth-of-type(2) {
	order: 1;
}

.day:last-of-type {
	order: 2;
}

#panelModule {
	width: 300px;
}

.day .temperatures .plustemp {
	color: #ff6666 !important;
}

.day .temperatures .minustemp {
	color: #7fb5e6 !important;
}

.mail a img,
.kontakt a img {
	display: none;
}

.mail a::after,
.kontakt a::after {
	content: "";
	display: block;
	background-position: center;
	background-size: cover;
	width: 30px;
	height: 30px;
	margin-top: 20px;
	filter: invert(100%);
	margin-right: 20px;
}

#blog a:link,
#blog a:visited {
	color: #0078C8;
	text-decoration: underline;
}

.mail a::after {
	background-image: url("https://img.icons8.com/material/48/000000/important-mail.png");
}

.kontakt a::after {
	background-image: url("https://img.icons8.com/material/48/000000/phone--v1.png");
}

#container {
	border: thin solid #333 !important;
	margin: 0 auto;
}

#wrapper #container .logo img {
	display: none;
}

#wrapper #container .logo a::before {
	content: "";
	display: block;
	background-image: url("http://www.vp.fo/fileadmin/templates/favicon/android-icon-192x192.png");
	background-position: center;
	background-size: cover;
	width: 150px;
	height: 93px;
}

#wrapper #container .logo img,
#wrapper #container #topheader div img {
	filter: invert(80%);
}

#tx_indexedsearch #searchField,
#tx_indexedsearch #searchSubmit,
.day,
.categor a {
	background-color: black;
}

.day .wind {
	filter: invert(100%);
}
#tx_indexedsearch #searchField {
	border: 1px solid black;
}

#tx_indexedsearch #searchSubmit {
	height: 18px;
	border-left: 1px solid gray;
	transition: all .2s;
}

#tx_indexedsearch #searchSubmit:hover {
	background-color: #444;
}

#topdato2 {
	height: 32px;
}

.textwidget,
.ModulePage {
	background-color: #333333;
}

.facebook iframe {
	background-color: #AAA;
}

.lightbox,
.wp-post-image,
.sidebar img {
	opacity: 0.6;
	transition: all .2s;
}

.lightbox:hover,
.wp-post-image:hover,
.sidebar img:hover {
	opacity: 1;
}
}
