css:
@document domain("stackoverflow.com")
{
#mainbar, .mainbar {
    width: 100%;
}

#sidebar, .sidebar {
    display: none;
}

.bottom-notice.bottom-share-links {
display: none;
}

.top-bar {
    border-top: 3px solid #333333;
    background-color: #242729;
}

.top-bar .-marketing-link {
    color: #a8b0b7;
    background: #4a4a4a;
}

.top-bar .my-profile .-rep {
    color: #909fad;
}

.top-bar .-logo .-img {
    filter: invert(1);
}

.top-bar a.-logo:hover {
    background-color: rgba(56, 56, 56, 0.75);
}

.top-bar .my-profile:hover {
    background-color: #2f2f2f;
}

.s-btn__primary, .s-btn__primary:focus {
    background-color: #434b50;
	box-shadow: none;
}

input[type="submit"]:not(.s-btn), input[type="button"]:not(.s-btn), button:not(.s-btn), .button, a.button, a.button:visited, .btn, .hero-box.double-panel .panel.white .btn {
    color: #FFF;
    background-color: #515d65;
    border-color: #58646d;
    box-shadow: inset 0 1px 0 #495258;
}

.question-status {
    background-color: #f7f7f7;
}


input[type="submit"].btn-secondary, input[type="button"].btn-secondary, button.btn-secondary, .btn-secondary {
    color: #5b6871;
    background-color: #ffffff;
    border-color: #aeb8bf;
    box-shadow: inset 0 1px 0 #f4f8fb;
}

#mainbar.ask-mainbar, .mainbar.ask-mainbar {
    width: 100%;
}

textarea.wmd-input, textarea#wmd-input {
    height: 430px;
}

.tagged-interesting {
    background-color: #fdfdfd;
}

ul.comments-list .comment-body {
    word-wrap: break-word;
    padding: 10px 0;
}

}
