css:
@-moz-document domain("python.gel.ulaval.ca") {
/* Primary Background Color */

body,
.navbar-default .navbar-nav>.open>a, 
.navbar-default .navbar-nav>.open>a:focus, 
.navbar-default .navbar-nav>.open>a:hover,
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
    background-color: #333;
}

#header {
    background-color: #333 !important;
}

/* Secondary Background Color*/

.navbar-default,
.breadcrumb,
.dropdown-menu,
.panel-default,
.thread, 
.reply {
    background-color: #242424;
}

.bg-default {
    background-color: #101010;
}
/* Primary Font Color */

body,
.navbar-default .nav>li>a,
.dropdown-menu>li>a {
    color: #CCC;
}

a[href="http://www.gelgif.ulaval.ca"] {
    color: #CCC !important;
}

/* Secondary Font Color */

a[href="http://www.fsg.ulaval.ca"] {
    color: #ACACAC !important;
}

/* Font colored */

.breadcrumb a, 
.prevnext a {
    color: #ACACAC;
}
.text-success {
    color: #63C264;
}

ins, .badge-teacher>.fa.fa-thumbs-o-up {
    color: #101010;
}

.table>tbody>tr.warning>td, .table>tbody>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>thead>tr>td.warning, .table>thead>tr>th.warning {
    background-color: #695C1F;
}
/* White Font Color */

.text-muted,
.dropdown-header,
.navbar-default .navbar-nav>li>a:focus,
.navbar-default .navbar-nav>li>a:hover,
.navbar-default .navbar-brand:hover,
.navbar-default .navbar-brand:focus,
.navbar-default .navbar-nav>.open>a, 
.navbar-default .navbar-nav>.open>a:focus, 
.navbar-default .navbar-nav>.open>a:hover,
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
    color: #E6E6E6;
}

/* Border */

.panel-default,
.thread, 
.reply{
    border-color: #333;
}

/* ULaval Logo */

img[alt="Universit√© Laval"] {
    content: url("https://svgshare.com/i/DUy.svg");
}

/* Scrollbars */

::-webkit-scrollbar {
    background-color: transparent;
}

::-webkit-scrollbar-thumb {
    background-color: #575757;
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.5);
}

/* Form Input */

select {
    background-color: #575757;
    border-color: #333;
}
.form-control {
    background-color: #575757 !important;
    color: #E6E6E6;
    border-color: #333;
}
.form-control::placeholder {
    color: #ACACAC;
}

/* Buttons */

.btn-default {
    background-color: #7A7A7A;
    border-color: #333;
    color: #E6E6E6;
}
.btn-default:hover {
    border-color: #333;
}
.btn-primary {
    background-color: #286090;
    border-color: #333;
    color: #E6E6E6;
}
.btn-primary:hover {
    background-color: #337AB7;
    border-color: #333;
    color: #E6E6E6;
}
.btn-default:hover>i {
    color: #333;
}


.btn-default.active, 
.btn-default:active, 
.open>.dropdown-toggle.btn-default,
.btn-default.active.focus, 
.btn-default.active:focus, 
.btn-default.active:hover, 
.btn-default:active.focus, 
.btn-default:active:focus, 
.btn-default:active:hover, 
.open>.dropdown-toggle.btn-default.focus, 
.open>.dropdown-toggle.btn-default:focus, 
.open>.dropdown-toggle.btn-default:hover {
    color: #E6E6E6;
    background-color: #242424;
    border-color: #E6E6E6;
}

/* Font-Awesome */
.fa {
    color: #CCC;
}
.fa[class*="translucide"] {
    opacity: 1;
}
.red {
    color: #d9534f !important;
}
.fa-comment {
    color: #5EB85C;
}
.green {
    color: #5EB85C !important;
}
.gold {
    color: #FFd700 !important;
}
.blue {
    color: #337AB7 !important;
}
.silver {
    color: #bbb !important;
}
.widget-success {
    color: #5cb85c !important;
}
.widget-danger {
    color: #d9534f !important;
}

/* Some other shit */
#thread-history>div {
    background-color: #242424 !important;
}
a:hover,
a:focus {
    color: #63B6FF;
}
a {
    color: #44A1F2;
}
.nav>li>a:focus,
.nav>li>a:hover {
    background-color: #242424;
}

.table>tbody>tr.info>td, 
.table>tbody>tr.info>th, 
.table>tbody>tr>td.info, 
.table>tbody>tr>th.info, 
.table>tfoot>tr.info>td, 
.table>tfoot>tr.info>th, 
.table>tfoot>tr>td.info, 
.table>tfoot>tr>th.info, 
.table>thead>tr.info>td, 
.table>thead>tr.info>th, 
.table>thead>tr>td.info, 
.table>thead>tr>th.info,
.bg-info{
    background-color: #1C4263;
} /*12->72 97->39*/
.bg-warning {
    background-color: #695C1F;
}
.bg-danger {
    background-color: #5E1E1E;
}

/* Table */

.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #101010;
}
.table>tbody>tr>td, 
.table>tbody>tr>th, 
.table>tfoot>tr>td, 
.table>tfoot>tr>th, 
.table>thead>tr>td, 
.table>thead>tr>th {
    border-top-color: #101010;
}
.table>thead>tr>th {
    border-bottom-color: #101010
}

.sorting_1 {
/*     background-color: #332F2F; */
    background-color: transparent;
}
.pagination>li>a, 
.pagination>li>span {
    background-color: #101010;
    border-color: #332F2F;
}
.pagination>.disabled>a, 
.pagination>.disabled>a:focus, 
.pagination>.disabled>a:hover, 
.pagination>.disabled>span, 
.pagination>.disabled>span:focus, 
.pagination>.disabled>span:hover {
    background-color: #101010;
    border-color: #332F2F;
    color: #ACACAC;
}
.pagination>li>a:focus, 
.pagination>li>a:hover, 
.pagination>li>span:focus, 
.pagination>li>span:hover {
    background-color: #575757;
    color: #63B6FF;
    border-color: #ACACAC;
}

/* Pop-up */
#onesignal-bell-container.onesignal-reset 
.onesignal-bell-launcher 
.onesignal-bell-launcher-dialog {
    background-color: #101010;
}
#onesignal-bell-container.onesignal-reset 
.onesignal-bell-launcher.onesignal-bell-launcher-sm 
.onesignal-bell-launcher-dialog 
.onesignal-bell-launcher-dialog-body h1 {
    color: #e54b4d;
}

/* Code Block */
.mce-content-body *[data-mce-selected="inline-boundary"] {
/*     background-color: #BFE6FF; */
    background-color: #EEE;
}
code {
    background-color: #101010;
    color: #D02852;
}
code[class*="language-"], 
pre[class*="language-"] {
    background-color: #333 !important;
    color: #E5E5E5;
    text-shadow: 0 1px #666;
}
.token.operator, .token.entity, .token.url, .language-css .token.string, .style .token.string {
    background: inherit;
    color: #F2BB83;
}
.token.selector, .token.attr-name, .token.string, .token.char, .token.builtin, .token.inserted {
    color: #99E500;
}
.token.comment, .token.prolog, .token.doctype, .token.cdata {
    color: #ABC3DB;
}
.token.punctuation {
    color: #E5E5E5;
}
.token.property, .token.tag, .token.boolean, .token.number, .token.constant, .token.symbol, .token.deleted {
    color: #E5007E;
}
.token.atrule, .token.attr-value, .token.keyword {
    color: #00ADF7;
}
.line-numbers-rows > span:before {
    color: #E5E5E5;
}
pre {
    border-color: #4D4D4D;
}

/* Text Editor */
.modal-header {
    border-bottom-color: #101010;
}
.modal-footer {
    border-top-color: #101010;
}
.close {
    color: #CCC;
    opacity: 1;
}
.close:hover {
    color: #E6E6E6;
    opacity: 1;
}
.mce-btn {
    background-color: #101010;
}
.mce-tabs,
.mce-combobox input{
    border-color: #242424;
}
.mce-tab:focus {
    color: #E5E5E5;
}
.mce-tab {
    background-color: #242424;
    border-color: #101010;
}
.mce-tab:hover {
    background-color: #575757;
}
.mce-tab.mce-active {
    background-color: #333
}
.mce-panel {
    background-color: #101010;
    border-color: #242424;
}
.mce-btn-group:not(:first-child) {
    border-left-color: #242424;
}
.mce-btn:hover, .mce-btn:active, .mce-btn:focus {
    background-color: #333;
    border-color: #242424
}
.mce-ico {
    color: #CCC;
}
.mce-splitbtn.mce-opened, .mce-splitbtn:hover .mce-open {
    border-color: #242424;
}
.mce-floatpanel.mce-popover {
    background-color: #101010;
}
.mce-floatpanel.mce-popover.mce-bottom>.mce-arrow:after {
    border-bottom-color: #101010;
}
.mce-grid td.mce-grid-cell div {
    border-color: #333
}
.mce-colorbtn-trans div {
    color: #CCC;
}
i.mce-i-backcolor {
    background-color: #4D4D4D;
}
.mce-container, .mce-container *, .mce-widget, .mce-widget *, .mce-reset, .mce-window-head .mce-close i {
    color: #CCC;
}
.mce-textbox {
    background-color: #4D4D4D;
    border-color: #242424;
}
.mce-window-body .mce-btn, .mce-foot .mce-btn {
    border-color: #242424
}
.mce-window-head {
    border-bottom-color: #242424;
}
.modal-content {
    background-color:#242424;
}

.mce-tabs, .mce-tabs+.mce-container-body {
    background: #101010;   
}
/* Step by Step */

div.ExecutionVisualizer #vcrControls button {
    background-color:  #7a7a7a !important;
    border-color: #333;
    color: #101010
}
div.ExecutionVisualizer div#langDisplayDiv, div.ExecutionVisualizer div#stackHeader, div.ExecutionVisualizer div#heapHeader {
    color: #CCC;
}
.ui-resizable-e {
    border-color: #333;
}
.ui-resizable-e:hover {
    border-color: #101010;
}

div.ExecutionVisualizer div.highlightedStackFrame {
    background-color: #1C4263;
    border-color: #333
}
div.ExecutionVisualizer table.dictTbl td.dictKey, div.ExecutionVisualizer table.instTbl td.instKey, div.ExecutionVisualizer table.classTbl td.classKey {
    background-color: #5E1E1E;
    border-color: #333
}
div.ExecutionVisualizer table.classTbl, div.ExecutionVisualizer table.funcTbl {
    border-color: #333
}
div.ExecutionVisualizer table.dictTbl td.dictVal, div.ExecutionVisualizer table.instTbl td.instVal, div.ExecutionVisualizer table.classTbl td.classVal, div.ExecutionVisualizer td.funcCod {
    background-color: #101010;
    border-color: #333
}
div.ExecutionVisualizer .typeLabel {
    color: #E6E6E6;
}
.bg-info{
    background-color: #1C4263;
} /*12->72 97->39*/
.bg-warning {
    background-color: #695C1F;
}
}

@-moz-document domain("python-hub.gel.ulaval.ca") {
/* Section pour les Notebooks */
body>#header {
/*     background-color: #101010; */
    background-color: #242424;
}
body, #notebook, .notebook_app, #site {
    background-color: #242424;
}

#notebook-container {
    background-color: #101010;
}



div.cell.selected, div.cell.selected.jupyter-soft-selected {
    border-color: #333;
}
div.cell.selected:before, div.cell.selected.jupyter-soft-selected:before {
    background-color: #00ADF7;
}
div.text_cell_render {
    color: #CCC;
}
a:hover,
a:focus {
    color: #63B6FF;
}
a {
    color: #44A1F2;
}
/* Code Block */
code {
    background-color: #242424 !important;
    color: #D02852  !important;
}

div.input_area {
    background-color: #333 !important;
    border-color: #242424
}
.CodeMirror-linenumber {
    color: #ACACAC;
}
.CodeMirror-gutters {
    background-color: #333;
    border-right-color: #242424;
}
div.output_area pre {
    color: #ACACAC;
}
.CodeMirror pre{
    color: #CCC;
}
.CodeMirror div.CodeMirror-cursor {
    border-left-color: #E5E5E5;
}
.cm-s-ipython span.cm-variable {
    color: #E5E5E5;
}
.cm-s-ipython span.cm-variable-2 {
    color: #b79a00;
}
.cm-s-ipython span.cm-def {
    color: #44A1F2;
}

/* Button */
.btn-default, #btn_execute {
    color: #242424;
    background-color: #7A7A7A;
    border-color: #242424;
}
.btn-default {
    background-color: #101010;
    border-color: #333;
    color: #CCC;
}
.btn-default:hover {
    border-color: #333;
}
}
