/* ==UserStyle==
@name         codecov.io Dark
@namespace    gitlab.com/DX-MON
@version      0.0.7
@description  Dark theme for codecov.io
@author       Rachel Mant <dx-mon@users.sourceforge.net> (https://gitlab.com/DX-MON)
@homepageURL  https://github.com/DX-MON/codecov.io-dark
@updateURL    https://github.com/DX-MON/codecov.io-dark/blob/master/codecov.io.user.css
@license      GPLv3+
@preprocessor stylus
==/UserStyle== */
/* codecov.io dark theme by Rachel Mant (c) 2019 */
/* You may hereby use this theme under the terms of the GPLv3+ */

@-moz-document domain("codecov.io")
{
body, .ui.inverted.menu { background-color: #222; color: #ddd; }
.ui.inverted.menu { border-bottom: 1px solid rgba(255,255,255,.08); }
.nav { background-color: #222; border-bottom: 1px solid rgba(255,255,255,.08); }
.nav .ui.segments { border: solid rgba(255,255,255,.08); border-width: 1px 1px 0; border-radius: 0; }
.ui.segment, .ui.menu, .ui.table { background-color: #222; }
.nav .ui.ribbon { background-color: #111; border-color: rgba(255,255,255,.08); }
.ui.segments:not(.horizontal)>.segment:first-child { border-radius: 0; }
.nav .title { width: calc(100% - 18rem); }
a, .ui.info.message { color: #608dff; }
.ui.tabular.menu .item, .ui.menu .item, .ui.toggle.checkbox .box, .ui.toggle.checkbox label { color: rgba(255,255,255,.9); }
.ui.secondary.pointing.menu .active.item { color: rgba(255,255,255,.9); background-color: #333; border-color: rgba(255,255,255,.08); }
.ui.secondary.pointing.menu .dropdown.item:hover, .ui.secondary.pointing.menu .link.item:hover, .ui.secondary.pointing.menu a.item:hover,
	.ui.secondary.pointing.menu .active.item:hover { color: rgba(255,255,255,.8); background-color: #2E2E2E; border-color: rgba(255,255,255,.08); }
.nav .ui.segments>.ui.segment:last-child { border-bottom: 1px solid rgba(255,255,255,.08); }
.ui.secondary.pointing.menu { margin-bottom: -1px; }
.ui.attached.menu:not(.tabular), .totals .button>.button, .ui.attached.segment, .src .src-n, .src .src-d { border-color: rgba(255,255,255,.08); }
.ui.breadcrumb > a > svg, .ui.menu a.item > svg, table#tree tr th > svg, .ui.items > .item a > svg,
	.ui.piled.segments > .ui.segment > svg, .ui.container h1 > svg { fill: rgba(255,255,255,.9); }
.ui.breadcrumb .divider { color: rgba(255,255,255,.9); }
.ui.menu .item:before { background-color: rgba(255,255,255,.08); }
.ui.toggle.checkbox .box:before, .ui.toggle.checkbox label:before { background: #111; border: 1px solid rgba(255,255,255,.16); }
.ui.toggle.checkbox .box:hover::before, .ui.toggle.checkbox input:focus~.box:before, .ui.toggle.checkbox input:focus~label:before, .ui.toggle.checkbox label:hover::before {
	background: #444; border: 1px solid rgba(255,255,255,.16);
}
.ui.piled.segment:after, .ui.piled.segment:before, .ui.piled.segments:after, .ui.piled.segments:before { background-color: #333; }

.totals .tiny.button { color: #ddd; }
.ui.pink.label, .ui.pink.labels .label { background-color: #CE1A5C !important; }
.totals .tiny.button.null { background-color: #1C1C1C !important; }
.ui.button.light-green { background-color: #1c5720; }
.totals .tiny.button.tr.p2 { background: linear-gradient(90deg, #56A03B 100%, black 100%) !important; }
.src:not(.src-diff).src-m-o tr.src-cm { background-color: #4E1B1B; }
.src:not(.src-diff).src-h-o tr.src-ch { background-color: #284C28; }
.src:not(.src-diff).src-p-o tr.src-cp { background-color: #565600; }

.ui.green.label, .ui.green.labels .label
{
	vertical-align: bottom;
	color: #ddd !important;
	background-color: #00751B !important;
	line-height: 1;
	font-size: .65rem;
	padding: 0.35rem !important;
	width: 18px;
	height: 18px;
}

.ui.sortable.table thead th, .ui.table { color: rgba(255,255,255,.9); }
.ui.table thead th, .ui.table tfoot th { color: rgba(255,255,255,.9); border-color: rgba(255,255,255,.08); background-color: #222; }
.ui.selectable.table tbody tr:hover, .ui.table tbody tr td.selectable:hover,
	.ui.sortable.table thead th:hover { color: rgba(255,255,255,.8) !important; background-color: #1E1E1E !important; }

.card>.header { color: rgba(255,255,255,.9); background-color: #333; }
.ui.card, .ui.cards>.card { background-color: #222; box-shadow: 0 1px 3px 0 rgba(255,255,255,.08), 0 0 0 1px rgba(255,255,255,.08); }
.ui.card>.content>a.header, .ui.cards>.card>.content>a.header, .ui.items>.item>.content>a.header { color: rgba(255,255,255,.9); }
.ui.card>.extra a:not(.ui), .ui.cards>.card>.extra a:not(.ui), .ui.card>.extra, .ui.cards>.card>.extra { color: rgba(255,255,255,.9); }
.ui.card>.extra a:not(.ui) > svf, .ui.cards>.card>.extra a:not(.ui) > svg, .ui.card>.extra.content > div > svg,
	.ui.card .meta > a > svg, .ui.info.message svg { fill: rgba(255,255,255,.9); }
.sunburst path { stroke: rgba(255,255,255,.32); }
.ui.card>.content>.header, .ui.cards>.card>.content>.header { color: rgba(255,255,255,.9); }
.ui.card .meta, .ui.cards>.card .meta, .ui.card>.content>.description, .ui.cards>.card>.content>.description { color: #ddd; }

.ui.basic.button, .ui.basic.buttons .button { background-color: #333 !important; color: #ddd !important; box-shadow: 0 0 0 1px rgba(255,255,255,.08) inset; }
.ui.basic.button:focus, .ui.basic.button:hover, .ui.basic.buttons .button:focus, .ui.basic.buttons .button:hover {
	background-color: #444 !important; color: #eee !important; box-shadow: 0 0 0 1px rgba(255,255,255,.08) inset;
}
.ui.comments .comment .metadata, .ui.comments .comment .author, .ui.comments .comment .actions a { color: #9e9e9e; }
.ui.comments .comment .actions a.active, .ui.comments .comment .actions a:hover { color: rgba(255,255,255,.9); }

.ui.header { color: #ddd; }
.ui.selection.dropdown, .ui.dropdown .menu, .ui.dropdown .menu>.item, .ui.dropdown .menu .selected.item,
	.ui.dropdown.selected, .ui.selection.visible.dropdown>.text:not(.default) { color: rgba(255,255,255,.9); background-color: #2E2E2E; }
.ui.selection.active.dropdown, .ui.selection.active.dropdown .menu, .ui.selection.active.dropdown:hover,
	.ui.selection.active.dropdown:hover .menu, .ui.selection.dropdown .menu>.item { border-color: rgba(255,255,255,.08); }
.ui.selection.dropdown:hover, .ui.selection.dropdown:focus { border-color: rgba(255,255,255,.2); }

.ui.inverted.dimmer { background-color: rgba(0,0,0,.5); }
.ui.inverted.dimmer .ui.loader { color: rgba(255,255,255,.9); }
.ui.inverted.dimmer .ui.loader:before { border-color: rgba(255,255,255,.15); }

.ui.info.message { background-color: #1C1C1C !important; box-shadow: 0 0 0 1px rgba(255,255,255,.08) inset, 0 0 0 0 transparent; }
.ui.info.message  > .ui.divider { color: rgba(255,255,255,.9); }
}
