css:
@-moz-document domain("moodle.xamk.fi") {
body,body#page-mod-assign-view
{
    background-color: black;
    color: white;
    background-image: url(https://i.imgur.com/ccAmZz9.png);
}

div#page-navbar,
tr[style='background-color: #fbba18;'] > td,
.jumbotron-mustard
{
    background-color: #c99103;
}

.xamk-footer, .xamk-footer h1, .xamk-linkbox H3
{
    color: #c99103;
}

.course-content .current
{
    border-color: #c99103;
}

.jumbotron, .xamk-fp-navbar .xamk-fp-navbar-inner
{
    background-color: rgba(0, 59, 81, .8);
}
.jumbotron-grey
{
    background-color: rgba(142, 141, 141, .8);
}

.xamk-navbar-inner
{
    background-image: url(https://i.imgur.com/zHg9P83.jpg);
}

.block, .block .header, .block .header .block_action, .xamk-white-bg, .course-content ul li.section.main, .popover, .popover-title, .moodle-actionmenu[data-enhanced].show .menu, .popover-region-container, .popover-region-footer-container, .dropdown-menu
{
    background-color: #4d4d4d;
    color: white;
}
.content-item-container.unread
{
    background-color: rgba(200,200,200,0.3);
}

.course-content ul li.section.main
{
    border-bottom: none;
}


/*temp force rule*/
table.generaltable tr > td,
table.forumheaderlist tr > td
{
    background-color: #4d4d4d !important;
    color: white !important;
}


.block .header .title h2, .block h3, .moodle-actionmenu[data-enhanced].show .menu a, .content-item-container .content-item-footer .timestamp, .dropdown-menu > li > a
{
    color: white;
}

a, a:visited { color: #00a2d7; }
a:active, a:hover, a:focus { color: #00b0e8; }
.breadcrumb a, .breadcrumb a:visited, .breadcrumb a:active, .breadcrumb a:focus, .breadcrumb a:hover { color: black; }
.xamk-footer a, .xamk-footer a:visited, .xamk-footer a:active, .xamk-footer a:focus, .xamk-footer a:hover { color: white; }

textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input,
.block_myoverview .well,
table.table-bordered tr,
.filemanager .fp-navbar,
.filepicker-filelist, .filemanager-container,
.filepicker-filelist .dndupload-target, .filemanager-container .dndupload-target,
.filepicker-filelist.dndupload-over .dndupload-target, .filemanager-container.dndupload-over .dndupload-target,
#fgroup_id_buttonar,
.fp-iconview .fp-filename-field .fp-filename,
.forumpost,
select,
.editor_atto_content_wrap,
div.editor_atto_toolbar
{
    background-color: #333333;
    color: white;
}

.filemanager .yui3-datatable-header,
.filemanager .yui3-datatable-even .yui3-datatable-cell,
.filemanager .yui3-datatable-even .yui3-datatable-cell > span > a,
.fp-treeview span
{
    background-color: #333333 !important;
    color: white !important;
}

.filepicker-filelist .dndupload-target, .filemanager-container .dndupload-target
{
    box-shadow: 0 0 0 10px #333333;
}
.fp-iconview .fp-thumbnail { border: none; }

.btn,
.nav-item > a:hover, .nav-item > a:active, .nav-item > a:focus,
.nav-tabs > li > a:hover, .nav-tabs > li > a:active, .nav-tabs > li > a:focus,
.openlinkbrowser,
button.submit,
input[type="submit"]
{
    background-image: linear-gradient(to bottom,#515151,#1c1c1c);
    color: white;
}

.btn:hover, .btn:focus, .btn.active,
.nav-item.active > a, .nav-item.active > a:hover, .nav-item.active > a:active, .nav-item.active > a:focus,
.nav-tabs > .active > a, .nav-tabs > .active > a:hover, .nav-tabs > .active > a:focus,
.openlinkbrowser:hover, .openlinkbrowser:active, .openlinkbrowser:focus,
button.submit:hover, button.submit:active, button.submit:focus,
input[type="submit"]:hover, input[type="submit"]:active, input[type="submit"]:focus
{
    background-position: initial;
    background-image: linear-gradient(to bottom,#3e3d3d,#000);
    transition: background-image 0.3s linear;
    color: #ddd;
}

.section .activity.modtype_label.label *[style*="background-color:"]
{
    background-color: #333333 !important;
    color: white !important;
}
.section .activity.modtype_label.label h1[style*="background-color: white;"]
{
    background-color: black !important;
    color: white !important;
}
.section .activity.modtype_label.label ul[style*="color: #555;"]
{
    color: white !important;
}

h4[style*="color:"], h3[style*="color:"], h2[style*="color:"], h1[style*="color:"]
{
    color: white !important;
}

.fp-toolbar .fp-btn-add, .fp-toolbar .fp-btn-download, .fp-toolbar .fp-btn-mkdir, .fp-toolbar .fp-tb-help, .fp-toolbar .fp-tb-manage, .fp-toolbar .fp-tb-logout, .fp-toolbar .fp-tb-refresh,
.fp-viewbar,
div.editor_atto_toolbar button,
.editor_atto_toolbar button.highlight
{
    background-color: #111;
    background-image: none;
}
div.editor_atto_toolbar button[disabled]
{
    background-color: #000;
    opacity: 0.7;
}
.fp-toolbar .fp-btn-add:hover, .fp-toolbar .fp-btn-download:hover, .fp-toolbar .fp-btn-mkdir:hover, .fp-toolbar .fp-tb-help:hover, .fp-toolbar .fp-tb-manage:hover, .fp-toolbar .fp-tb-logout:hover, .fp-toolbar .fp-tb-refresh:hover,
.fp-toolbar .fp-btn-add:active, .fp-toolbar .fp-btn-download:active, .fp-toolbar .fp-btn-mkdir:active, .fp-toolbar .fp-tb-help:active, .fp-toolbar .fp-tb-manage:active, .fp-toolbar .fp-tb-logout:active, .fp-toolbar .fp-tb-refresh:active,
.fp-toolbar .fp-btn-add:focus, .fp-toolbar .fp-btn-download:focus, .fp-toolbar .fp-btn-mkdir:focus, .fp-toolbar .fp-tb-help:focus, .fp-toolbar .fp-tb-manage:focus, .fp-toolbar .fp-tb-logout:focus, .fp-toolbar .fp-tb-refresh:focus,
.fp-viewbar a.checked, .fp-viewbar a.checked:hover, .fp-viewbar a.checked:active, .fp-viewbar a.checked:focus,
.fp-viewbar a:active, .fp-viewbar a:hover, .fp-viewbar a:focus,
div.editor_atto_toolbar button:hover, div.editor_atto_toolbar button:active, div.editor_atto_toolbar button:focus
{
    background-image: none;
    background-color: #222;
}
.fp-toolbar a:hover,
.fp-toolbar a:active,
.fp-toolbar a:focus
{
    background-image: none;
    background-color: transparent;
}

.moodle-dialogue-base .moodle-dialogue-wrap
{
    background-color: #444;
    color: white;
}
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd, .moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd
{
    background-image: -moz-linear-gradient(top,#555,#333);
    background-image: -webkit-gradient(linear,0 0,0 100%,from(#555),to(#333));
    background-image: -webkit-linear-gradient(top,#555,#333);
    background-image: -o-linear-gradient(top,#555,#333);
    background-image: linear-gradient(to bottom,#555,#333);
    color: white;
}

.moodle-dialogue-base .moodle-dialogue .moodle-dialogue-bd,
.mform legend.ftoggler a, .mform legend.ftoggler a:hover
{
    color: white;
}
}
