@-moz-document domain("www.youtube.com") {
/* üëãüèΩ Hello, fellow web developer! üëãüèΩ */

/* Initial setup */
:root {
	--color: #7d20d4;
	--standard-ease: cubic-bezier(0.4, 0.0, 0.2, 1)
}

* {
	outline: none
}

/* Scrollbar */
[dark] body::-webkit-scrollbar {
	width: 15px !important;
	height: 15px !important;
	display: block !important;
	background: #141414 !important;
	background-image: url(data:image/svg+xml;base64,), url(data:image/svg+xml;base64,) !important;
	background-position: top center, bottom center !important;
	background-size: contain !important;
	background-repeat: no-repeat !important;
