css:
@-moz-document domain("trello.com") {
#content {
    position: unset !important;
}

#surface>#header {
    width: 100px !important;
}

/* Bigger columns*/

.list-wrapper {
    width: 420px !important;
}

/* Small Icebox, actually first column*/
.js-list:first-child{
	width: 280px !important;
}

.js-list:first-child .badges, .js-list:first-child .list-card-members{
	display: none !important;
}

.list-card-members .member{
	  height: 22px !important;
    width: 22px !important;
}

/* Card font size*/
.list-cards {
    margin: 0 2px !important;
    padding: 0px 1px !important;
    font-size: 13px !important;
}

.list-card {
    max-width: none !important;
}

.list-card-title {
    clear: both !important;
    display: block !important;
    margin: 0 0 2px !important;
    overflow: hidden !important;
    text-decoration: none !important;
    word-wrap: break-word !important;
    color: #172b4d !important;
    line-height: 18px !important;
}

/* Small labels*/
.card-label.mod-card-front {
    font-size: 8px !important;
}

#header, .header-btn, .board-header-btn  {
    height: 22px !important;
}

.icon-lg {
    height: 20px !important;
    font-size: 12px !important;
    line-height: 20px !important;
    width: 30px !important;
}

.header-btn.header-boards {
    padding-left: 0px !important;
}

#board {
    margin-bottom: -7px !important;
}

.board-header-btn-icon, .board-header-btn-icon-close {
    padding: 2px !important;
}

.header-logo, .header-btn-text, .header-btn.header-avatar, .header-search, .header-info, .js-open-add-menu, .js-rename-board, .js-star-board, .board-header-btn-divider, .js-open-org-menu, .board-header-facepile, .mod-left, .board-header-btn-text, #header{
    display: none !important;
}

.badge[title="Attachments"], .badge[title="Comments"], .badge[title="You are watching this card."], .badge[title="Trello attachments"]{
	 display: none !important;
}

.badge{
	margin: 0 4px 0px 0 !important;
}

.card-label.mod-card-front {
    min-width: 30px !important;
}

text .card-label.mod-card-front {
height: 15px !important;
    line-height: 15px !important;
    padding: 0 5px !important;
    text-align: center !important;
}

.list-header {
    padding: 5px 6px !important;
}

.board-header {
    padding: 0px 0px 2px 8px !important;
}

.member-avatar {
    height: 22px !important;
    width: 22px !important;
}

.pop-over-member-list div li a span img, .js-card-detail-members-list div img {
    height: 32px !important;
    width: 32px !important;
}

.badge-text {
    font-size: 11px !important;
}
}
