css:
@-moz-document url-prefix("https://ci-en.dlsite.com/") {
body {
    padding: 0;
    background-color: #2a2a2a;
    color: #222;
    font-family: -apple-system, BlinkMacSystemFont, "Helvetica Neue", "Noto Sans Japanese", "ヒラギノ角ゴ ProN", Meiryo, sans-serif;
    font-size: 1.6rem;
    position: relative;
    height: 100%;
}
.globalNav-wrap {
    background-color: #3d3d3d;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    width: 100%;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: end;
    -webkit-justify-content: flex-end;
    -ms-flex-pack: end;
    justify-content: flex-end;
}
.header-inner {
    background-color: #3d3d3d;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    position: relative;
    height: 44px;
    line-height: 44px;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    border-bottom: 1px solid #ddd;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
    -ms-flex-pack: justify;
    justify-content: space-between;
}
.n-contentsHeading {
    background-color: #3d3d3d;
    width: 100%;
    font-size: 1.8rem;
}
.type-bluebar {

    height: auto;
    min-height: 34px;
    line-height: 34px;
    padding-left: 10px;
    border-left: 5px solid #238ac5;
}
.n-whiteBox .n-innerWhiteBox {
    border: 1px solid #ddd;
    padding: 15px;
}
* {
    background-color: #3d3d3d;
    color: #222;
    outline: 0;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}
p {
    font-size: 1.3rem;
    line-height: 1.9rem;
    color: #5ce217;
}
h1 {
    font-weight: 700;
    font-size: 1.6rem;
    line-height: 1.9rem;
    margin: 0;
    color: #5ce217;
}
.sideNavList-item > .link {
    display: inline-block;
    position: relative;
    width: 100%;
    padding: 15px 20px 15px 50px;
    color: #5ce217;
    font-size: 1.4rem;
}

a {
    color: #01dc4e;
    text-decoration: none;
}
.moreView.type-center {
    text-align: center;
}
.moreView {
    background-color: #616161;
    display: block;
    padding: 15px;
    border: 1px solid #14ef00;
    border-top: none;
    font-size: 1.3rem;
}
.n-btn.type-follow {
    width: 100%;
    background-color: #616161;
    border: 2px solid #00ff6e;
    color: #35f728;
    text-align: center;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
}
.global-layout-item.type-contents {
    color: #3d3d3d;
    grid-row: 4/5;
    -ms-grid-row: 5;
}
.n-articleCard-item.type-creatorName a {
    color: #00f14a;
}
.contents-wrap {
    border: 1px solid #2fff09;
    background-color: #3d3d3d;
    margin: 0 0 15px;
}
.inner-searchOptionList-item.is-selected a {
    color: #01ffa1;
    font-weight: 700;
}
.inner-searchOptionList-item a {
    display: block;
    font-weight: 700;
    width: 100%;
    padding: 5px;
    color: #009c6b;
}
.n-icon.type-comment,
.n-icon.type-pv,
.n-icon.type-vote,
.n-icon.type-voteCount {
    padding: 0 5px;
    border-style: solid;
    border-width: 1px;
    background-color: #6d6d6d;
    height: 22px;
    line-height: 20px;
    font-size: 1.2rem;
}
.n-btn {
    display: inline-block;
    position: relative;
    text-align: center;
    padding: 10px 5px;
    border: 1px solid #ddd;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 -2px 0 rgba(0, 0, 0, .2);
    box-shadow: inset 0 -2px 0 rgba(0, 0, 0, .2);
    background-color: #616762;
    color: #333;
    cursor: pointer;
    font-size: 1.3rem;
    -webkit-transition: .1s ease;
    transition: .1s ease;
}
.n-btn.type-min-good {
    width: 40px;
    height: 40px;
    padding: 0;
    border-color: #00fc54;
    color: #00fc54;
    -webkit-box-shadow: none!important;
    box-shadow: none!important;
}
.n-btn.type-min-good.is-on {
    padding: 10px;
    background-color: #4c9600;
}
@media screen and (min-width: 768px) {
    .searchResultList {
        margin: 0;
        padding-top: 15px;
        border: 1px solid #ddd;
        border-top: none;
        background-color: #3d3d3d;
    }
}
.searchResultList .searchResultList-item {
    margin-top: 10px;
    background-color: #3d3d3d;
    margin-bottom: 10px;
}
.n-contentsHeading {
    color: #01ffa1;
    font-size: 2rem;
    margin-bottom: 0;
}
.disp-searchCondition {
    color: #01ffa1;
}
.disp-searchCondition span {
    color: #01ffa1;
}
b,
strong {
    font-weight: bolder;
    color: #3ed8ff;
}
.n-tag.type-creator {
    display: inline-block;
    height: 22px;
    padding: 0 4px;
    border: 1px solid #ff00f2;
    border-radius: 2px;
    background-color: #31e99f;
    color: #000;
    font-size: 1.5rem;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    line-height: 20px;
    text-decoration: none;
    -webkit-transition: .15s ease;
    transition: .15s ease;
}
.n-tag.type-activityGenre {
    display: inline-block;
    padding: 0 4px;
    border: 1px solid #ff00f2;
    border-radius: 2px;
    background-color: #31e99f;
    color: #000;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    text-decoration: none;
    -webkit-transition: .15s ease;
    transition: .15s ease;
    min-height: 22px;
    font-size: 1.5rem;
    line-height: 20px;
}
.grid-creatorCard-item.item-description {
    color: #01ffa1;
    grid-row: 3/4;
    -ms-grid-row: 3;
    grid-column: 1/5;
    -ms-grid-column: 1;
    -ms-grid-column-span: 4;
}
.n-viewSwitch .n-viewSwitch-item {
    overflow: hidden;
    width: 100%;
    border: 1px solid rgba(0, 0, 0, .12);
    background-color: #5d5b5b;
    color: #14ff72;
    font-size: 1.3rem;
    border-left: none;
}
.n-btn.type-follow.is-followed,
.n-btn.type-follow.is-on {
    border: 2px solid #e013ff;
    color: #000;
    text-shadow: 0.5px 0.5px #2f2f2f;
    background-color: #5dff80;
    font-size: 1.5rem;
}
.inner-planCard {
    padding: 15px;
    border-radius: 2px;
    background-color: #3d3d3d;
}
.subscription .inner-planCard {
    background-color: #3d3d3d;
}
.inner-userInfoList-item {
    font-size: 1.2rem;
    margin-bottom: 5px;
    color: #0f0
}
.noticeList-item {
    padding: 10px;
    margin-bottom: 25px;
    border: 1px solid #ddd;
    background-color: #4c4b4b;
}
.globalNav-icon {
    padding-left: 30px;
    display: block;
    width: auto;
    color: #2aa12a
}
.hd-searchInput {
    width: 130px;
    height: 50px;
    border: 1px solid #4bff01;
    border-radius: 10%;
    border-bottom: 1px solid #4bff01;
    font-size: 1.3rem;
}
* {
    background-color: #3d3d3d;
    color: #00ff42;
    outline: 0;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}
.main-container.type-guide {
    margin: 0 0 15px;
    padding: 40px;
    background-color: #3d3d3d;
}
.pageHeading {
    height: 70px;
    padding: 0 20px;
    font-weight: 400;
    font-size: 2rem;
    line-height: 70px;
    background-color: #3d3d3d;
}
.sideGuideMenuList .sideGuideMenuList-item .link {
    display: block;
    padding: 10px 0 10px 20px;
    color: #d21eff;
}
.n-flexbox.type-articleList.type-newArrival {
    border-top: 1px solid #00fc54;
}
.n-flexbox.type-articleList {
    -webkit-flex-wrap: wrap;
    -ms-flex-wrap: wrap;
    flex-wrap: wrap;
    border: 1px solid #00fc54;
    border-top: 1px solid #00fc54;
    background-color: #00fc54;
    border-bottom: 1px solid #00fc54;
}
.downloadBlock {
    background-color: #3d3d3d;
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -webkit-justify-content: space-between;
    -ms-flex-pack: justify;
    justify-content: space-between;
    -webkit-box-align: center;
    -webkit-align-items: center;
    -ms-flex-align: center;
    align-items: center;
    padding: 15px;
}
.articlePager-item.type-prev {
    padding: 14px 20px 14px 14px;
    background-color: #434343;
    border: 2px solid;
}
.is-sealed .rewardBox-heading {
    color: #ccb573;
    margin: 0;
    background-color: #3d3d3d;
}
.rewardBox .btn.type-unique {
    max-width: 200px;
    border: solid 2px #f0f
}
.btn.type-unique {
    width: 80%;
    margin: 0 0 0 10px;
    height: 40px;
    line-height: 40px;
    -webkit-box-shadow: inset 0 -2px 0 rgba(0, 0, 0, .2);
    box-shadow: inset 0 -2px 0 rgba(0, 0, 0, .2);
    border: none;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#f100ff), to(#52ff00));
    background-image: linear-gradient(to bottom, #ffda25, #fff);
    background-image: -moz-linear-gradient(top, #ffda25, #00ff6a);
    background-image: -webkit-gradient(linear, center top, center bottom, from(#f2ff0080), to(#0c871bdb));
    color: #f100ff;
    font-size: 1.4rem;
    font-weight: 600;
}
ul.sns-buttons li .snsCount {
    padding: 5px 20px;
    text-align: center;
    color: #00ff42;
    background-color: #3d3d3d;
    border-radius: 0 0 3px 3px;
}
* {
    background-color: #3d3d3d;
    color: #00ff42;
    outline: 0;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}
.followYet {
    padding: 15px 20px;
    border: 1px solid #2fff09;
    background-color: #3d3d3d;
    border-radius: 4px;
}
.nav-content.type-left {
    left: 0;
    transform: translateX(-105%);
    -webkit-transform: translateX(-105%);
    background-color: #3d3d3d;
}
.nav-drawerList-item a {
    display: block;
    padding: 15px 25px 15px 50px;
    color: #02d84d;
    font-size: 1.5rem;
    text-decoration: none;
    line-height: normal;
}
.global-layout-item.type-header {
    height: 50px;
    border-bottom: 1px solid #2fff0900;
    background-color: #464646b5;
}
.globalNav-icon {
    padding-left: 30px;
    display: block;
    width: auto;
    border: solid 2px #2fff0973;
    border-radius: 25%;
    border-width: 1.5px;
}
.globalNav-item a {
    padding: 0 12px;
    height: 50px;
    border-bottom: 2px solid #ff00dabd;
    font-size: 1.6rem;
}
.is-user .globalNav-item.type-bell {
    display: inline;
    border-radius: 50%;
    background-color: #3b3b3b;
    color: #3b3b3b
}
.surveyBox {
    margin-top: 15px;
    border: 1px solid #2fff09;
    background-color: #3d3d3d;
    padding: 15px;
}
.surveyBox > .details p span {
    width: 6em;
    color: rgb(14, 225, 62);
    margin: 1px 8px 0 0;
}
.n-btn.type-basic {
    background-image: -webkit-gradient(linear, center top, center bottom, from(#3d3d3d), to(#504f4f00));
    color: #fff;
    border: solid 2px #00b3ff;
}
.u-inlineBlock {
    display: inline-block!important;
    max-width: 100%;
    color: #ff00db
}
.eisysGroupHeaderAccountBox-inner .eisysGroupHeaderLoginAccount {
    background: #3d3d3d;
    padding: 12px 10px;
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.6;
    border: solid 1px;
    border-radius: 5%;
    background-color: #252525
}
.eisysGroupHeaderAccountBox-inner .eisysGroupHeaderLinkNav > li > a {
    display: block;
    position: relative;
    padding: 12px 10px;
    font-weight: bold;
    color: #0fff18;
    text-decoration: none;
    border: solid 0.5px;
    border-radius: 5%;
    background-color: #252525
}
.eisysGroupHeaderAccountBox-inner .eisysGroupHeaderLinkNav {
    padding: 0px 0;
}
.eisysGroupHeaderAccountBox {
    border: 0px solid #e616e00d;
    border-radius: 2%;
    -webkit-box-shadow: 0 5px 10px rgba(0, 0, 0, .96);
    box-shadow: 0 5px 10px rgb(0, 0, 0);
    position: relative;
    background: #fff;
}
.postedArticleList-item .type-articleTitle:visited {
    color: #9a29d2;
}
.postedArticleList-item .postDate {
    color: #0bce35;
}
* {
    background-color: #3d3d3d;
    color: #000;
    outline: 0;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    margin: 2;
    padding: 0;
}
.icon.type-vote,
.icon.type-voteCount {
    padding: 0 5px;
    border-style: solid;
    border-width: 1.5px;
    border-color: #ffb300;
    background-color: #fff;
    height: 22px;
    line-height: 22px;
    font-size: 1.3rem;
}
.icon.type-comment,
.icon.type-pv,
.icon.type-tweetCount,
.icon.type-vote,
.icon.type-voteCount {
    padding: 0 5px;
    border-style: solid;
    border-width: 1.5px;
    background-color: #fff;
    height: 22px;
    line-height: 22px;
    font-size: 1.3rem;
}
.articlePager-item.type-next {
    padding: 14px 14px 14px 20px;
    background-color: #434343;
}
.btn.type-basic {
    border: none;
    -webkit-box-shadow: inset 0 -2px 0 rgba(0, 0, 0, .2);
    box-shadow: inset 0 -2px 0 rgba(0, 0, 0, .2);
    background-image: -webkit-gradient(linear, left top, left bottom, from(#34a8eb), to(#2b98d6));
    background-image: linear-gradient(to bottom, #34a8eb, #2b98d6);
    background-image: -moz-linear-gradient(top, #34a8eb, #2b98d6);
    background-image: -webkit-gradient(linear, center top, center bottom, from(#34a8eb), to(#73c1ec));
    color: #fff;
    font-size: 1.4rem;
}
.side-column .inner-planCard {
    padding: 20px;
    background-color: #3d3d3d;
}
.accountInfo .accountInfo-item h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 3px;
    color: #ff00d0;
    text-shadow: 1px 1px #000;
}
.side-column .type-creatorName {
    color: #ff00d0!important;
    font-weight: 700;
    font-size: 1.6rem;
    text-shadow: 2px 2px #000;
}
.n-flexbox.type-articleList .n-flexbox-item.type-blank {
    -webkit-box-flex: 1;
    -webkit-flex: auto;
    -ms-flex: auto;
    flex: auto;
    background-color: #3d3d3d;
}
.n-notApplicable {
    margin: 15px;
    padding: 15px 10px;
    border-radius: 4px;
    background-color: #5d5b5b;
}
.n-articleRankingCard-item.type-postTitle a .articleTitle {
    font-size: 1.6rem;
    line-height: 1.9rem;
    color: #00ff00f0
}
.n-articleRankingCard-item.type-creatorName a {
    color: #1dd50b;
}
.registrationYet {
    padding: 15px 20px;
    border-radius: 4px;
    background-color: #3d3d3d;
}
.articlePager-item.type-latestArticle a {
    padding: 10px;
    text-shadow: 1.5px 1.5px #000000db;
    color: #48c42c;
    font-weight: 700;
    background-image: -webkit-gradient(linear, center top, center bottom, from(#0af), to(#001ec6));
}
.article-title a {
    color: #14d74b;
    font-size: 2.5rem;
    text-shadow: 1px 1px #000;
    line-height: 2.4rem;
    font-weight: 800;
}
.type-postDate a {
    font-size: 1.3rem;
    color: #f909d0;
    text-shadow: 1px 1px #000
}
/**
     * ::-webkit-scrollbar-*
     */
::-webkit-scrollbar-track {
    background-color: var(--color-background-2);
}
::-webkit-scrollbar {
    width: var(--width-scrollbar);
    background-color: var(--color-background-2);
}
::-webkit-scrollbar-thumb,
.yt-scrollbar-dark.ytd-playlist-panel-renderer::-webkit-scrollbar-thumb {
    background: #00ff7b;
    background: -webkit-gradient(linear, left bottom, right top, color-stop(0%, #007761), color-stop(50%, #26ffc5), color-stop(100%, #00ffa9));
}
/* width */
::-webkit-scrollbar {
    width: 5px;
}
}
