css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mudconnect.com") {
    body,html { background: #000 !important; }
    div#middle { width: 100% !important; }
    div#headerwrap, div#footerwrap { position: static !important; background: #000 !important; }
    div#middlewrap { padding: 0px !important; }
    div#footer div#irebutton { height: auto !important; }

    div#header { width: 898px !important; background-position: -1px 0px !important; }
    div#footer { background: #033 !important; border: 1px solid #fff !important; width: auto !important; padding-top: 20px !important; }
    blockquote { background-color: #001A33; padding: 1em; border: 1px solid #fff; }
    div#content textarea { width: 900px !important; }
    div#shadedbox2 { padding: 0px 1em 1em !important; }

    div#content > center > table:first-child > tbody > tr:first-child > td:first-child > table:first-child { background: #033 !important; width: 900px !important; padding: 10px; border: 1px solid #fff !important; }
    div#buttonbar { position: absolute !important; right: 30px; top: 210px; width: 70px; border: 1px solid #fff; padding: 20px; background: #333; }
    div#buttonbar > nobr > a, div#buttonbar > nobr > img { float: left; clear: left; margin-top: 10px !important; }
    div#buttonbar > nobr > a:first-child, div#buttonbar > nobr > img:first-child { margin-top: 0px !important; }

    div#adbar, 
    div#content > script:first-child + link + br + center + br,
    div#content > script:first-child + link + br + center + br + center > table:first-child,
    div#content > center + center > table:first-child tr:first-child > td:first-child > center > p + table,
    div#content > center + center > table:first-child tr:first-child > td:first-child > center:first-child + br,
    div#content > center + center > table:first-child tr:first-child > td:first-child > center:first-child + br + p,
    div#content > center + center > table:first-child tr:first-child > td:first-child > center:first-child + br + p + center + br,
    div#content > center + center > table:first-child tr:first-child > td:first-child > center:first-child + br + p + center + br + a,
    div#content > center + center > table:first-child tr:first-child > td:first-child > center:first-child + p + table[align="center"],
    /** Edit Page */
    div#content > center + center > table:first-child tr:first-child > td:first-child > center:first-child + table[width="90%"] { display: none !important; }

    div#content > center + center > table:first-child tr:first-child > td:first-child > center + br + center > table:first-child tr:first-child > td:first-child > ul > ul,
    div#content > center + center > table:first-child tr:first-child > td:first-child > div[id^="thread_"] > center > table { width: 900px !important; }

/** Edit Page */
    div#content > center + center > table:first-child tr:first-child > td:first-child > center + table + p + center + center > table:first-child tr:first-child > td:first-child > font > center + p + center > table[border="1"] { width: 840px !important; background: #333; padding: 30px !important; border: 1px solid #fff !important; }

/** Main Discussion Page Width */
div#content > script + link + br + center + br + center + center > table:first-child tr:first-child > td:first-child > a + center > table.table { width: 70% !important; }

/** Front Page Fixes */
div#frontLatestDisc { width: 900px !important; }
div#frontLatestDisc > link + table[width="90%"] { width: 100% !important; }
div#content > div#adbar + center > table:first-child > tbody > tr:first-child + tr + tr > td > div[style] { margin: 0px auto !important; }

/** Discussion Index Page */
div#content > center + center > table:first-child > tbody > tr:first-child > td:first-child > br + center > table[width="95%"] {
 background: #000 !important; border: 1px solid #fff !important; border-collapse: collapse !important; }
div#content > center + center > table:first-child > tbody > tr:first-child > td:first-child > br + center > table[width="95%"] > tbody > tr:first-child + tr > td { background: #111 !important; }

/** Listing Search Results */
div#content > center + br + p + center > form:first-child > table[width="80%"] { border-collapse: collapse !important; border: 1px solid #fff !important; }
div#content > center + br + p + center > form:first-child > table[width="80%"] > tbody > tr:first-child + tr { background: #111 !important; }
}
