css:
@-moz-document domain("support.apple.com") {
body,
.alt-light {
    background-color: #333;
}

#globalfooter-wrapper,
.code-source {
    filter: invert(80);
}

.contenttable-title,
.contenttable-section-title,
p,
h2,
h1,
#ac-globalnav .ac-gn-link,
#ac-gn-link-search, .localnav-title, .localnav-title > a, .localnav-menu-link {
    color: azure;
}

#ac-globalnav {
    background-color: #444;
}
}
