css:
@-moz-document url-prefix("http://www.planalto.gov.br/ccivil_03/") {
html {
    display: flex;
    align-items: center;
    align-self: center;
    justify-content: center;
    overflow-x: hidden;
}

* {
    font-family: Baskerville, Constantia, Cambria, serif !important;
    font-size: 1.2rem;
}

body {
    background-color: #333;
    color: azure !important;
    max-width: 50%;
}

a {
    color: darkgreen !important;
}

a:visited {
    color: chocolate;
}

span,
p,
font,
b,
p span,
font > p > span,
small {
    color: azure !important;
    font-size: 1.2rem;
}

td {
    background-color: #333;
}

strike, strike > font {
    color: gray !important;
}
}
