css:
@-moz-document url-prefix("https://www.yahoo.co.jp/") {
/* 変数 */
:root {
    --color-sub: #f9f9f9;
    --color-main: #61e89f;
}

/* すべての要素に適用 */
* {
    font-family: "Meiryo", sans-serif !important;
    font-size: 16px !important;
    border-width: 0px !important;
    border-radius: 8px !important;
}

/* 例外 */
._3WrFs0ChLr_s9-gBqS7yts {
    border-radius: 0 !important;
}

.PHOgFibMkQJ6zcDBLbga8,
.Bg8j2T4mhSQsnpFn6ZtwQ {
    color: #fff !important;
}

/* 要素非表示 */
#eyebrow,
._ksk75rJuVj51SK_eWKdU,
.QoCp7KDEt29FbN5hCjsCx{
    display: none;
}

/* 横幅調整 */
#wrapper {
    width: 90%;
}

/* パディング調整 */
._171Q0OxQF2Vk3J_CxMxajb {
    padding: 8px !important;
}

._1o9PYyvuVafb5hd9eJ9rYX {
    padding: 32px;
}

._3DayOTWaRJVwUMySMNY4MZ {
    padding: 0px !important;
}

/* マージン調整 */
#Masthead,
._1CYhoJYsAsnHPIGbcii0Z5 {
    margin-bottom: 16px !important;
}

#Masthead {
    margin-top: 16px;
}

/*トップバー調整*/
._1OWcOh2V41fyCNvgz4p7rf {
    width: 1152px;
}

._2dMkuiRLjRf9kTmHJL1EZX:not(:last-child) {
    margin-right: 32px;
}

/* ロゴのカスタマイズ */
/* (仕様変更によりコメントアウト中)
#Masthead h1:after {
    color: var(--color-main);
    font-family: "Century Gothic", "Futura", sans-serif;
    font-size: 64px;
    content: "Yahoo!";
}
*/

/* 検索バーにシャドウ追加 */
._1wsoZ5fswvzAoNYvIJgrU4,
._63Ie6douiF2dG_ihlFTen {
    filter: drop-shadow(8px 8px 8px #00000010);
}

/* 検索バーのサイズ調整 */
._1f9dgjwXCsoV3gv3CR4ed0 {
    width: 768px;
}

._1CYhoJYsAsnHPIGbcii0Z5 {
    margin-right: 256px;
}

._63Ie6douiF2dG_ihlFTen {
    width: 192px;
}

._1wsoZ5fswvzAoNYvIJgrU4,
._63Ie6douiF2dG_ihlFTen {
    min-height: 42px !important;
}

/* 色の上書き */
._63Ie6douiF2dG_ihlFTen {
    background-color: var(--color-main) !important;
}

._1o9PYyvuVafb5hd9eJ9rYX,
._2hSsMnP8D1RrJLv1rfzExy:before,
._1fWTm2E47ymhMM3suCaCGi:after,
._171Q0OxQF2Vk3J_CxMxajb,
._2hSsMnP8D1RrJLv1rfzExy:before,
._3KxBQ9kjbz4FI25qkyAiJg,
._1EYwdR4JxrwgpIvWk7vO3f,
._3wZKJtfV8AYYaTP-zoyO9O,
#Service,
#Scoreboard,
#Realtime{
    background: var(--color-sub) !important;
    border: none !important;
}

a,
span {
    color: #262626 !important;
}

}
