    color: var(--text-color);
}

#toppanel {
    background: var(--text-bg-gray-color);
}

#toppanel .panelitems .panelitem {
    color: var(--text-color);
}

body {
    color: var(--text-less-color);
    background-color: var(--dark-bg-color);
}

#author .mworkwrapper {
    background-color: var(--dark-bg-color);
}

#lenta .shares .share {
    background-color: var(--text-bg-color);
}

