    color: var(--post-header-color);
}

#lenta .shares .share .sys span {
    color: var(--post-timestamp-color);
}

#lenta .shares .share .panel .itemwr>div.count span {
    color: var(--post-rating-color);
}

#lenta .shares .share .metatable>div.metapanel .metapanelitem {
