}

#comments .list .comment {
    border-bottom: 1px solid var(--dark-bg-color);
    border-bottom-color: var(--dark-bg-color);
}

.commentPaddingWrapper {
    background-color: var(--text-darker-bg-color);
}

b {
    color: var(--profile-text-color);
}

.fa-paper-plane:before {
    filter: invert(70%);
}

textarea {
    background-color: black;
}

#comments .answer .txt textarea, #comments .edit .txt textarea, #comments .post .txt textarea {
    border: 1px solid var(--text-bg-color);
}

.theme-background-color {
    background-color: var(--blue-tab-color);
}

/*-- Main Stream --*/
#main {
    background: var(--dark-bg-color);
}

#chat .headerWrapper {
