}

.fa-external-link-alt:before {
    filter: invert(100%);
}

.fa-image:before {
    filter: invert(100%);
}

.fa-book-open:before {
    filter: invert(100%);
}

#lenta .loadmore, #lenta .loadprev, #lenta .seemore {
    background: var(--text-darker-bg-color);
}

/*BEGIN 2019-09-09*/
#authorization .maket.leftPart {
