}

.checkbox+label:before {
    background: #000;
    border: 1px solid var(--text-bg-gray-color);
}

#notifications .nwrapper .notification {
    border-bottom: 1px solid var(--text-bg-gray-color);
    border-bottom-color: var(--text-bg-gray-color);
}

.lighttooltip .tooltipster-box {
    border: 1px solid var(--text-bg-gray-color);;
}

