#userslist {
    background: var(--text-bg-color);
}

/*Manage*/
#userpage .bgCaption {
    background: var(--text-bg-color);
}

#test {
    background: var(--text-bg-color);
}

button, input[type=text], input[type=email], input[type=password], input[type=number] {
    background: black;
    color: var(--text-color);
}

#test .userPanel .parameter .vc_valuecustom .vc_textInput, #test .userPanel .parameter .vc_valuecustom_multibig .vc_textInput {
    background: var(--dark-bg-color);
}

