}

.vc_valuecustom .vc_selectInput {
    background: var(--dark-bg-color);
}

textarea {
    color: var(--blueish-text);
}

#test .adressesInput .addedAddressesWrapper .addedAddressWrapper {
    background: var(--dark-bg-color);
}

#userpage .bgCaption {
    color: var(--blueish-text-lighter);
}

#share .tgs .tag {
    background: #000;
}
/*END 2019-09-09*/

/*BEGIN 2019-09-09b*/
#post .share {
    background: var(--text-bg-color);
}

.wnd.postwindow .wndcontent {
    background: var(--dark-bg-color);
    background-color: var(--dark-bg-color);
}

#post .share .commentsWrapper {
    background: var(--text-darker-bg-color);
    background-color: var(--text-darker-bg-color);
}

#post .share .sys span {
    color: var(--green-mint-text)
}

/*BEGIN 2019-09-10*/
/*Background panel color of comment entry.*/
#comments .list .comment .answer {
    background: var(--text-darker-bg-color);
    background-color: var(--text-darker-bg-color)
}
