}

#comments .caption {
    background: var(--text-bg-gray-color);
}

/*BEGIN Donate page*/
#donations .splits>div.dsplit {
    background: var(--text-bg-color);
    background-color: var(--text-bg-color);
}

#donations .supporting {
    background: var(--text-darker-bg-color);
    background-color: var(--text-darker-bg-color);
}

#donations .ways {
    background: var(--text-darker-bg-color);
    background-color: var(--text-darker-bg-color);
}

#donations .address {
    background: var(--text-darker-bg-color);
    background-color: var(--text-darker-bg-color);
}

#donations .address .addressline {
    background: var(--text-bg-color);
}
/*END Donate page*/

/*To Top button*/
.upbutton .mini {
    color: black;
    border: black;
    background: var(--text-bg-gray-color);
}

/*Notifications page*/
#userpage .maketsWrapper .report #notifications {
