}

button.ghost {
    color: var(--text-dimmer-color);
}

/*Reputation page*/
#content #ustate {
    background: var(--text-bg-color);
}

/*Wallet page*/
#wallet .deposit {
    background: var(--text-darker-bg-color);
}
#wallet .slide .subcaption span {
    color: var(--green-mint-text);
}
/*Can't CSS the top circle graphs because they're drawn with code with white backgrounds.*/
#wallet .slide {
    color: var(--blueish-text-medium);
}

/*Settings page*/
#usersettings {
    background: var(--text-bg-color);
}

#usersettings .mwork.wh {
    background: var(--text-bg-gray-color);
}

#usersettings .mwork.gk {
    border-bottom: 1px solid var(--text-bg-color);
}

/*Accounts page*/
#accounts {
    background: var(--text-bg-color);
}

/*Schedule page*/
#scheduler .shareTimeWrapper .shareContainer {
    background: var(--text-bg-color);
}
#scheduler .shareTimeWrapper .shareContainer {
    border-bottom-color: var(--text-bg-color);
}

/*Node setup page*/
#post .share .sharecaption {
    color: var(--blueish-text-lighter);
}

/*applicatoins page*/
#applications .os {
    background: var(--text-bg-color);
}
#applications .number {
